INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2, 'Глюк', 'Glitch', '', 2, 4, 1, 'В коньячном бокале смешать самбуку и абсент, поджечь и, дав чуть погореть, перелить в старомодный бокал, накрыв сверху перевернутым коньячным бокалом.  Когда огонь погаснет, коньячный бокал перенести на салфетку горлом вниз. Выдохнуть, залпом выпить содержимое старомодного стакана и первый вдох сделать парами из коньячного стакана.', null, 0, null, true, false, false, 0, 'brandy');
