INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (8, 'Ходули', 'Stilts', '', 2, 4, 4, 'В одну рюмку налить банановый ликер (Pisang Ambon) и наслоить на него по барной ложечке текилу. Во вторую рюмку налить самбуку и наслоить на нее по барной ложечке абсент. Пить коктейль следует сразу из двух рюмок, через две соломинки (ходули), шот с самбукой можно еще и поджечь.', null, 0, null, false, true, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (9, 'Месть за Минет', 'BlowJob Revenge', '', 2, 4, 4, 'В две отдельные рюмки налить айриш крем и сок лайма. Айриш крем выпить из рюмки и распространить его по всей полости рта, проглотить. И как можно скорее, следом выпить сок лайма из второй рюмки.   Ответ на вопрос о названии коктейля придет сам собой! Для пущего эффекта можно при употреблении встать на колени.', null, 0, null, false, false, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (10, 'Рыжая Собака', 'Red Dog', '', 2, 4, 4, 'В стопку налить 25 мл Самбуки, затем осторожно, не смешивая, при помощи ложечки наслоить 25 мл Текилы, после чего добавить 5 капель соуса Табаско. Если всё сделано аккуратно, то капли соуса остановятся на границе между Текилой и Самбукой.Коктейль смотрится чрезвычайно эффектно, но будьте осторожны! Рыжая собака больно кусает за язык!', null, 0, null, false, true, false, 50, 'shot');
