INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (196, 'Вкус Измены', 'Taste of Treason', '', 2, 2, 4, 'Процедить в бокал и подавать отжав и добавив небольшую апельстновую корочку.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (197, 'Ананасовый Чай', 'Pineapple Tea', '', 2, 2, 4, 'В шейкере со льдом смешать сахарную пудру и сок лимона до растворения сахара, перелить в стакан. Затем в стакан налить водку, текилу, джин, ром, трипл сек и ананасовый сок. Подавать, украсив долькой лимона и с соломинкой.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (198, 'Влажная Киска', 'Wet Pussy', '', 2, 2, 4, 'В стакане для смешивания со льдом смешать виски и амаретто, перелить в бокал и дополнить энергетическим напитком.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (199, 'Ангельское Лицо', 'Angel Face', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин и яблочный бренди (кальвадос). Процедить в бокал и подавать.', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (200, 'Влечение', 'Affinity', '', 2, 4, 4, 'Встакане для смешивания со льдом смешать виски, сухой вермут, сладкий вермут и Ангостуру. Процедить в бокал, украсить коркой лимона и подавать.', null, 0, null, false, false, false, 60, 'martini');
