INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (219, 'Аляскинский Апельсин', 'Alaskan Orange', '', 2, 2, 4, 'Смешать трипл сек, бренди и мороженое с половиной стакана колотого льда, перелить в чашу и украсить тертым шоколадом. По вкусу, можно добавить тертый мускатный орех.', null, 0, null, false, false, false, 0, 'bowl');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (220, 'Водородная Бомба', 'Hydrogen Bomb', '', 2, 2, 4, 'В стакан со льдом налить водку, сок лимона и дополнить лимонадом. Украсить кружком из лимона и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (221, 'Апельсиново-кровавый', 'Blood Orange Cocktail', '', 2, 4, 4, 'В шейкере, заполненном колотым льдом, встряхните все компоненты. Перелейте в бокал "Мартини" или любой другой, подходящий по объёму, и, украсив спиралькой из цедры апельсина, подавайте.
