INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (270, 'Гагарин', 'Gagarin', '', 2, 4, 4, 'Смешать водку, шери бренди, смородиновый ликер (Сreme de cassis) и сок лайма, перелить в коктейльный бокал и дополнить его шампанским. Подавать с коктейльной вишней.', null, 0, null, false, false, false, 0, 'martini');
