INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (233, 'Аллигатор', 'Alligator', '', 2, 2, 4, 'Наполнить половину стакана льдом, влить в него дынный ликер (Midori) и дополнить апельсиновым соком.', null, 0, null, false, false, false, 0, 'highball');
