INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (290, 'Гвоздик Удачи', 'Lucky Stud', '', 2, 4, 4, 'В стакане для смешивания со льдом перемешать бренди и Галиано в равных частях. Процедить в рюмки и подавтаь.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (237, 'Аперитиво', 'Aperitivo', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, самбуку и немного апельсинового биттера, процедить в бокал, украсить и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (238, 'Восхитительно Шипучий Коктейль', 'Celestial Fizz Cocktail', '', 2, 2, 4, 'В стакане смешать коньяк, клюквенный сок и ликер Гран Марнье. Дополнить охлажденным шампанским, украсить тоненьким кружочком из лимона и подавать.', null, 0, null, false, true, false, 0, 'bowl');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (241, 'Конец Времен', 'End Of The Time', '', 2, 2, 4, 'В шейкере со лбдом встряхнуть водку, коньяк и апельсиновый сок. Процедить в бокал для шампанского, дополнить шампанским и подавать.', null, 0, null, false, false, false, 50, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (242, 'Восход Солнца', 'Sunrise Morning', '', 2, 2, 4, 'Дополнить бокал апельсиновым соком и подавать.', null, 0, null, false, false, false, 0, 'wine');
