INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (245, 'Арктическая Бомба', 'Arctic Bomba', '', 2, 4, 4, 'В стопку охлажденную в морозильнике налить половину холодного 75° рома и половину холодного Канадского виски.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (246, 'Восход Солнца на Варадеро', 'Varadero Sunrise', '', 2, 2, 4, 'В бокал налить гренадин и на него наслоить апельсиновый сок. Аккуратно добавить несколько кубиков льда. Отдельно смешать голубой Кюрасао и светлый ром. Наслить смеь рома и ликера в стакан на апельсиновый оск и подавать с соломинкой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (247, 'Арктический Поцелуй', 'Arctic Kiss', '', 2, 2, 4, 'В бокал для шамапнского налить клюквенную водку (ледяно - кристальную водку, настоенную на спелых ягодках клюквы, выросшей коротким солнечным летом на берегх Арктики), охлажденную в морозильнике и дополнить шампанским (искрящимся и сияющим пузырьками, как севрное сияние над северным океаном), охлажденным в холодильнике.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (248, 'Восходящее Солнце', 'Rising Sun', '', 2, 2, 4, 'Украсить вишенкой и долькой лимона.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (250, 'Все, что Я Хочу - Этот Шот', 'All I Want Is This Shot', '', 2, 4, 4, 'В рюмку налить апельсиновый ликер, на него наслоить айриш крем и на него - текилу.', null, 0, null, false, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (252, 'Встретимся Завтра', 'Let&#039;s Meet Tomorrow', '', 2, 4, 4, 'Коньячную рюмку накрыть салфеткой, чтобы не выходили пары и залпом выпить содержимое бокала для шампанского. Вдохнуть пары из под салфетки и попращаться со разумом до завтра.', null, 0, null, true, false, false, 0, 'champagne');
