INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (289, 'Б - 51', 'B - 51', '', 2, 4, 4, 'В рюмку налить кофейный ликёр Калуа, затем наслоить Айриш крем, и, в конце, ром.', null, 0, null, false, true, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (254, 'Встряска', 'Earth Shake', '', 2, 2, 4, 'В блендере взбить до до однородного состояния одну морковь, чашку ананасового сока и половину чашки свежего очищенного ананаса на средней скорости. Можно добавить лёд.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (255, 'Армейский Зеленый', 'Army Green', '', 2, 4, 4, 'Название коктейль получил за его цвет.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (291, 'Реальный Б-52', 'Real B-52', '', 2, 4, 4, 'Поджечь и подавать. Пить горящим через сосломинку.', null, 0, null, true, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (256, 'Вуду', 'VooDoo', '', 2, 2, 4, 'В блендере взбить самбуку, арахисовый, дынный и клубничный ликеры с мороженным. Перелить в бокал, украсить клубникой и подавать с соломинкой.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (257, 'Арно', 'Arnaud', '', 2, 4, 4, 'Перелить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (258, 'Вулкан', 'Volcano', '', 2, 2, 4, 'Водку подогреть, градусов до 50. Налить в бокал и поджечь, влить в нее голубой кюрасао и дополнить шампанским. Подавать.', null, 0, null, false, false, false, 0, 'bowl');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (259, 'Арнольд Палмер', 'Arnold Palmer', '', 2, 2, 4, 'В бокал со льдом налить лимонад и аккуратно, стараясь не перемешивать дополнить бокал холодным чаем. Перед употреблением перемешать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (261, 'Артемка', 'Artemka', '', 2, 2, 4, 'В шейкере со льдом встряхнуть апельсиновый и ананасовый соки и грендаин. Процедить в бокал, дополнить охлажденным фруктовым киселем и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (262, 'Вынос с трех ударов', '3 Strikes, You&#039;re Out', '', 2, 4, 4, 'Налить в три отдельных рюмки три ликера и по очереди быстро их выпить.   Лучше, чтобы мятный ликёр был последним.  Чем меньше промежуток между глотками, тем лучше.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (263, 'Аруба', 'Aruba', '', 2, 4, 4, 'В бокал со льдом налить малиновую водку, малиновый ликер, плеснуть гренадина, перемешать и дополнить Спрайтом или 7Up. Украсить вишенкой и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (265, 'Атеистский Лучший', 'Atheist&#039;s Best', '', 2, 2, 4, 'В бокал налить две части водки, по одной части сока лимона и вишневого сока и две части шампанского.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (266, 'Гавайский Лавовый Поток', 'Hawaiian Lava Flow', '', 2, 2, 4, 'Украсить стакан свежей клубникой и подавать с соломинкой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (267, 'Атомная Бомба', 'Atom Bomb', '', 2, 4, 4, 'В старомодный бокал налить энергетический напиток, а в 4 рюмки текилу, абсент, ром и Ягермастер. Установить рюмки пирамидкой на вершине стакана с энергетиком и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (268, 'Гаванский Пляж', 'Havana Beach', '', 2, 4, 4, 'В стакане со льдом смешать светлый ром, ананасовый сок и сахарный сироп (Gomme syrup). Процедить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (271, 'Атомный Арбуз', 'Atomic Watermelon', '', 2, 4, 4, 'В шейккере со льдом встряхнуть водку, дынный ликер и кисло-сладкий микс, процедитьв бокал и подавть.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (334, 'Голая Жопа', 'Bare Ass', '', 2, 4, 4, 'В шейкере со льдом встряхнуть персиковый шнапс, Амаретто и ананасовый сок, процедить в рюмку и подавать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (273, 'Атомный Кот', 'Atomic Cat', '', 2, 2, 4, 'Украсить и подавать с соломинкой.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (274, 'Галантный Бык', 'Gallant Bull', '', 2, 2, 4, 'В стакан со льдом налить ликер Гальяно (Galliano) и дополнить энегетическим напитком (Red Bull)', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (275, 'Аустер', 'Oyster', '', 2, 4, 4, 'В широкую рюмку налить немного растительного масла, распределить его по стенкам ровным слоем. добавить целый яичный желток, ложку джина, пол ложки томатной пасты (можно заменить несколькими каплями Табаско)на кончике ложки соли и по 1/8 ложки красного и черного перца. Выжать маленькую дольку лимона и пить залпом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (276, 'Гальяно Хотшот', 'Galliano Hotshot', '', 2, 4, 4, 'Чуть присыпать тертым мускатным орехом и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (278, 'Гангрин', 'Gangreen', '', 2, 4, 4, 'В шейкере со льдом смешать равные части Ягермастера, айриш крема и мятного ликера. Процедить в рюмки и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (279, 'Аххххх', 'Ahhhhh', '', 2, 2, 4, 'Встакан наполнить льдом. Налить в него апельсиновый, ананасовый и клюквенный соки, дополнить имбирным элем и подавать с соломинкой.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (280, 'Гангстер Колада', 'Gangsta Colada', '', 2, 4, 4, 'В бокал с колотым льдом налить коньяк, кокосовый ром и Спрайт или 7Up. Слегка перемешать и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (281, 'Ацтек', 'Aztec', '', 2, 2, 4, 'В бокал налить дынный ликер, текилу и дополнить шампанским.', null, 0, null, false, false, false, 60, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (305, 'Бабушка в Коляске', 'Grand-Ma In A Wheelchair', '', 2, 4, 4, 'Процедить в сауэр и подавать.', null, 0, null, false, false, false, 0, 'sower');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (306, 'Глаз Змеи', 'Snake Eye', '', 2, 2, 4, 'В стакан со льдом налить абсент, текилу, трипл сек и дополнить грейпфрутовым соком.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (282, 'Гарвард', 'Harvard', '', 2, 4, 4, 'В стакане для смешивания со льдом перемешать коньяк, вишневый ликер  (Maraschino Cherry Liqueur) красный сладкий вермут и плеснуть биттера  (Angostura). Процедить в коктейльный бокал, добавить маленькую корчку  лимона и украсить коктейльной вишней.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (283, 'Аэрофлот', 'Aeroflot', '', 2, 2, 4, 'Этот коктейль специально создан для пассажиров Аэрофлота, путешествующих бизнес классом на дальние расстояния.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (284, 'Гарибальди', 'Garibaldi', '', 2, 2, 4, 'В стакан со льдом налить Кампари и дополнить апельсиновым соком.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (285, 'Б - океан', 'B - ocean', '', 2, 2, 4, 'Украсить долькой лимона и листочками мяты, подавать!', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (286, 'Гарпун', 'Harpoon', '', 2, 4, 4, 'В рюмку налить равные части текилы и сока лимона, добавить несколько капель гренадина. Не размешивать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (287, 'Б и Б', 'B &amp; B', '', 2, 4, 4, 'В коньячном бокале смешать в равных пропорциях бренди и ликёр Бенедиктин.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (288, 'Гарри', 'Harry&#039;s Cocktail', '', 2, 4, 4, 'В стакане для смешивания со льдом интенсивно перемешать две веточки мяты, сладкий вермут, абсент и джин. процедить в бокал с оливкой и подавать. Можно еще украсить веточкой мяты.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (292, 'Доктор Гей', 'Dr. Gay', '', 2, 4, 4, 'В коктейльный бокал с колотым льдом налить одну часть золотого рома и три части колы Dr Pepper. Плеснуть гренадина и размешать. Добавить пару коктейльных вишенок и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (293, 'Б - 53', 'B - 53', '', 2, 4, 4, 'В рюмку слоями налить кофейный ликёр (Калуа), Самбуку и ликёр Grand Marnier.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (294, 'Гейша', 'Geisha', '', 2, 4, 4, 'В рюмке смешать виски и дынный ликер, затем долить ананасовый сок и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (297, 'Б-Кола', '', '', 2, 2, 4, 'В бокал, заполненный льдом, влить Бехеровку, Колу, и украсить ломтиками лимона.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (298, 'Гептил', 'Geptil', '', 2, 4, 4, 'В бокале смешать одну чать сухого белого вермута и две части шампанского.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (299, 'Баба Таня', 'Granny Tanja', '', 2, 2, 4, 'В блендер положить 6 кубиков льда и выжать четвертинку лайма, половинку очищенного киви и пару листочков мяты. Налить ванильную водку, белое сухое вино и кленовый сироп. Измельчить все в течении 30 секунд и процедить в коктейльный бокал. Дополнить шампанским и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (300, 'Гибсон', 'Gibson', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать джин и сухой белый вермут, процедит в коктейльный стакан и украсить одной или двумя маринованными коктейльными луковками насаженными на зубочистку.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (301, 'Бабоукладчик', 'Womanstacker', '', 2, 2, 4, 'В бокал налить Вана Таллин и дополнить его охлажденным шампанским.    У коктейля говорящее название и в комментариях не нуждается.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (302, 'Гидромель', 'Hidromel', '', 2, 2, 4, 'Внимание мое рецепт привлек потому, что его несколько раз пытались найти у нас на сайте. И для удовлетворения желаний наших посетителей мне пришлось найти рецепт и опубликовать здесь.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (303, 'Бабочка', 'Butterfly', '', 2, 2, 4, 'В блендере взбить до однородного состояния сороженое, молоко, мёд и банан. Перелить в бокал и подавать.', null, 0, null, false, false, false, 60, 'margarita');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (304, 'Гладиатор', 'Gladiator', '', 2, 2, 4, 'В высокий стакан со льдом налить виски и амаретто, затем апельсиновый сок и спрайт или 7 Up.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (307, 'Бабушкин Глюк', 'Grandma&#039;s Glitch', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (308, 'Глаз Шторма', 'Eye of the Storm', '', 2, 2, 4, 'Налить в большой, высокий стакан все компоненты, слегка перемешать и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (309, 'Бабушкина Конфетка', 'Granny Candy', '', 2, 4, 4, 'В коктейльный бокал положить несколько кубиков льда и влить равные части сухого вермута и Спрайта или 7 Up.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (311, 'Бабье Лето', 'Indian Summer', '', 2, 2, 4, 'В винный бокал налить Кампари и красный грейпфрутовый сок. Дополнить бокал белым игристом вином и подавать.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (313, 'Баварский Кофе', 'Bavarian Coffee', '', 2, 2, 4, 'В бокал налить мятный шнапс и кофейный крем ликер, добавить кофе. Сахар по вкусу. Перемешать и украсить взбитыми сливками. Сверху посыпать тертым шоколадом и подавать.', null, 0, null, false, true, false, 0, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (314, 'Глория', 'Gloria', '', 2, 4, 4, 'В стакан для смешивания налить виски, Кампари, сухой вермут и амаретто, дополнить льдом и перемешать. Процедить в коктейльный бокал. Украсить долькой карамболы и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (418, 'Горячие Головы', 'Hot Shots', '', 2, 4, 4, 'В рюмку налить лиекр Гальяно, наслоить на него горячий кофе и сверху добавить взбитые сливки.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (315, 'Бадуизм', 'Baduizm', '', 2, 4, 4, 'В шейкере со льдом встряхнуть лимончелло, светлый ром и сливки. Процедить в бокал и, посыпав тертым шоколадом, подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (316, 'Учебная глубинная бомба', 'Training Depth Charge', '', 2, 2, 4, '<strong>Как пить:</strong> Стопка топится в пивном бокале, и полученная смесь выпивается.', null, 0, null, false, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (317, 'Базиликовый Гранде', 'Basil Grande', '', 2, 4, 4, 'В чашу шейкера положите две ягоды клубники и два листочка свежего базилика и разомните их.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (318, 'Глупая Малина', 'Raspberry Stupid', '', 2, 2, 4, 'В бокал для шампанского налить малиновый ликер и дополнить его содовой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (319, 'Базука', 'Bazooka', '', 2, 4, 4, 'Пропорции можно подбирать на свой вкус.', null, 0, null, false, false, false, 40, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (320, 'Глушитель', 'Silencer', '', 2, 2, 4, 'В шейкере смешать и встряхнуть: 1 часть джина,1 часть светлого рома,1 часть крепкого рома,1 часть текилы,1 часть трипл сека,1 часть водки и 1 часть виски и 2 части кисло-сладкого микса (особенно умиляющий меня компонент в свете вышеперечисленного).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (322, 'Глэм Трэш', 'Glam Trash', '', 2, 4, 4, 'В рюмку налить две части коричного шнапса, наслоить на него одну часть светлого пива и капнуть гренадина.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (445, 'Бехефрут', 'Bechefruit', '', 2, 2, 4, 'Смешать бехеровку, сахарный сироп и грейпфрутовый сок, положить лёд, дополнить бокал содовой и украсить долькой грейпфрута.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (323, ' Байкальские Волны', 'Baikal Waves', '', 2, 2, 4, 'Вбокал налить две части напитка Байкал и одну часть светлого пива.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (325, 'Бакарди Спешиал', 'Bacardi Special', '', 2, 4, 4, 'Края коктейльного бокала смочить сиропом гренадин и обмокнуть в сахар, чтобы образовалась корочка.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (326, 'Гнилое Яблоко', 'Rotten Apple', '', 2, 4, 4, 'Пить залпом!
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (327, 'Бакэнир', 'Buccaneer', '', 2, 2, 4, 'Бакэнир (<i><span lang="en">Blackburn Buccaneer</span></i>) - Британский двухместный штурмовой самолет, состоявший на вооружении с 1962 по 1993 г.', null, 0, null, false, false, false, 0, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (328, 'Гобблетини', 'Gobble-tini', '', 2, 4, 4, 'Название - непереводимая игра слов, по-русски ближе всего по смыслу будет "курлык-мурлык".', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (329, 'Балалайка', 'Balalajka', '', 2, 4, 4, 'В шейкере со льдом смешать водку, трипл сек и сок лимона, процедить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (330, 'Зеленый Гоблин', 'Green Goblin', '', 2, 4, 4, 'В шейкере со льдом встряхнуть 30 мл зеленого Шартрёза, сок трети лайма и четверть ложечки сахарной пудры. Процедить в рюмку и подавать с долькой лайма.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (331, 'Балерина', 'Ballet Dancer', '', 2, 4, 4, 'В шейкере со льдом встряхнуть две части водки, одну часть сухого беслого вермута и одну часть трипл сек. Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1276, 'Пурпурная Корова', 'Purple Cow', '', 2, 2, 4, 'Подавать немедленно, с соломинкой и ложкой на длинной ручке.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (332, 'Годзилла', 'Godzilla', '', 2, 4, 4, 'В шейкере поджечь текилу, быстро влить в нее трипл сек, сок лимона, всыпать черный молотый перец и перелить в стакан. Немедленно подавать!', null, 0, null, true, false, false, 40, 'tumbler');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (333, 'Балрог', 'Balrog', '', 2, 4, 4, 'Пить залпом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (335, 'Баллистическая Банана', 'Ballistic Banana', '', 2, 4, 4, 'В стакане перемешать водку и банановый шнапс, перелить в коктейльный бокал и капнуть гренадина.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (336, 'Голливуд', 'Hollywood', '', 2, 2, 4, 'Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (339, 'Бананза', 'Bananza', '', 2, 2, 4, 'Для украшения можно использовать фрукты и ягоды, или тертый шоколад.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (340, 'Голограмма', 'Hologram', '', 2, 4, 4, 'В шейкере со льдом встряхнуть банановый ликер, голубой Кюрасао и клюквенный сок. Процедить в рюмку и подавать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (342, 'Голубая Гора', 'Blue Mountain', '', 2, 4, 4, 'Прекрасный коктейль с насыщеным вкусом и призывом к действию.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (343, 'Банановое Счастье', 'Banana Happiness', '', 2, 2, 4, 'В блендере взбить все компоненты, перелить в бокал и подавать.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (344, 'Голубая Елена', 'Blue Helena', '', 2, 4, 4, 'В шейкере со льдом хорошенько встряхнуть водку, мёд, голубой Кюрасао и сок лайма, процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (345, 'Банансовый', 'Banana', '', 2, 2, 4, 'В блендер поместить банан и яблочный сок, взбить в пюре. добавить мороженое и сливки, по немногу корицы, тертого мускатного ореха и ложку сахара. Взбить все до гомогенного состояния и перелить в бокал. Подавать с соломинкой.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (346, 'Голубая Кровь', 'Blue Blood', '', 2, 2, 4, 'В шейкере со льдом встряхнуть светлый ром (если есть, то лучше апельсиновый) и голубой Кюрасао. Процедить в бокал и аккуратно наслоить шампанское.', null, 0, null, false, true, false, 60, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (348, 'Голубая Лагуна № 1', 'Blue Lagoon No.1', '', 2, 2, 4, 'Положить в стакан 6 кубиков льда, затем влить компоненты и перемешать, спрайт добавить в конце.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (446, 'Гремлин', 'Gremlin', '', 2, 2, 4, 'В кружке Мэйсона с двумя чашками льда смешать канадский виски и сок лайма. Перемешать и подавать с соломинкой', null, 0, null, false, false, false, 0, 'mayson');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (350, 'Голубая Луна', 'Blue Moon', '', 2, 2, 4, 'В стакан со льдом добавть апельсиновый ликер, сок лайма и дополнить лимонадом (типа Миринды или Фанты) украсить кружком из апельсина и подавать', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (351, 'Банши', 'Banshee', '', 2, 4, 4, 'В шейкере со льдом встряхнуть банановый ликер, какако ликер (Крем де какао) и сливки. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (352, 'Голубая Птичка', 'Blue Bird', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, голубой Кюрасао, миндальный сироп и сок лимона. Процедить в бокал и подавать, украсив коркой апельсина.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (353, 'Барбара', 'Barbara', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, белый крем де какао и сливки. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (354, 'Голубая Текила', 'Dlue Teqila', '', 2, 4, 4, 'В стопку налить голубой Кюрасао и наслоить на него текилу. Подавать и пить залпом!', null, 0, null, false, true, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (355, 'Барби Шот', 'Barbie Shot', '', 2, 2, 4, 'В шейкере со льдом встряхнуть кокосовый ром, водку и клюквенный и апельсиновый сок. Процедить в бокал для шампанского и подавать.', null, 0, null, false, false, false, 50, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (969, 'Итальянский Валиум', 'Italian Valium', '', 2, 4, 4, 'В рюмку налить амаретто, дополнить джином и хорошенько перемешать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (356, 'Голубое небо # 2', '', '', 2, 2, 4, 'В шейкере, наполовину заполненном льдом, встряхнуть виски, светлый ром, Голубой Кюрасао, ананасовый сок. Перелить в стакан "Харикэйн" украсить кружочком апельсина, коктейльным зонтиком и подавать.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (357, 'Барни в Кислоте', 'Barney on Acid', '', 2, 4, 4, 'В рюмку налить голубой Кюрасао, Ягермастер и плеснуть немного клюквенного сока.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (358, 'Голубое Облако', 'Blue Cloud', '', 2, 2, 4, 'В блендере взбить голубой Кюрасао, амаретто и ванильное мороженое до гомогенного состояния. Перелить в коньячный бокал и украсить сверху взбитыми сливками. Украсить коктейльной вишней и подавать.', null, 0, null, false, false, false, 0, 'brandy');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (359, 'Золотой Барон', 'Gold Baron', '', 2, 4, 4, 'В бокальчик налить белый мятный ликер, и наслоить на него коричный шнапс. Он укладывается вторым слоем, но при перемешивании получается золотистый оттенок.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (360, 'Голубой Ангел', 'Blue Angel', '', 2, 4, 4, 'В шейкере со льдом встряхнуть коньяк, ликер Парфаит Амор (Parfait Amour), свежевыжатый лимонный сок и жирные сливки. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (361, 'Барракуда', 'Barracuda', '', 2, 4, 4, 'Налить в рюмку равные части водки, трипл сека и персикового шнапса.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (362, 'Голубой Богемский', 'Blue Bohemian', '', 2, 2, 4, 'Налить в бокал Бехеровку, светлый ром, голубой Кюрасао, апельсиновый сок и перемешать, дополнить бокал тоником. Украсить спиралькой из цедры апельсина, добавить коктейльную вишню и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (363, 'Барт Симпсон', 'Bart Simpson', '', 2, 4, 4, 'Вшейкере со льдом встряхнуть водку, дынный ликер и кокосовый ром. Процедить в большую стопку и подавать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (364, 'Голубой Бриз', 'Blue Breeze', '', 2, 2, 4, 'В бокал, наполненный льдом, налить ликёр Гипнотик, кокосовый ром, и долить до краёв ананасовым соком. Слегка перемешать, украсить долькой ананаса и спиралькой из цедры апельсина, и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (365, 'Бархатный Килт', 'Velvet Kilt', '', 2, 4, 4, 'В шейкере со льдом встряхнуть виски и ирисовый шнапс, процедить в бокал. Для украшения можно добавить коктейльную вишню или маленькую ириску.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (366, 'Голубой Гавайский классический ', 'Classic Blue Hawaiian', '', 2, 2, 4, 'В блендере со льдом взбить водку, ром, голубой курасао, ананасовый сок, сок лимона и сахарную пудру до гомогенного состояния и перелить в бокал.  Подавать с коктейльным зонтиком.   Считается, что именно такой рецепт Голубого Гавайского коктейля - оригинальный. Создание его приписывается Гавайскому бармену Гарри Йии (Harry Yee) в 1957 году в деревушке Хилтон (Теперь это курорт и SPA центр)', null, 0, null, false, false, false, 40, 'highball');
