INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (462, 'Оригинальный Грог', 'Original Grog', '', 2, 2, 4, 'В стакан положить кубик сахара, налить на него кальвадос. Залить стакан горячей водой так, чтобы струйка попадала на кубик сахара. Размешать остатки сахара и подавать. Для вкуса можно добавить сок лимона, бутончик гвоздики или корицу.', null, 0, null, false, false, false, 0, 'coffee');
