INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (499, 'Болтовня', 'Schmooze', '', 2, 2, 4, 'В стакан со льдом налить половину апельсинового сока и половину Спрайта или 7Up.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1632, 'Фредди Фадпукер', 'Freddy Fudpucker', '', 2, 2, 4, 'В стакан со льдом налить текилу, апельсиновый сок и сверху налить Гальяно.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (500, 'Дайте Мне Крылья!', 'Give Me Wings', '', 2, 2, 4, 'В стакане со льдом смешать Ягермастер, кофейный и персиковый ликер, дополнить энергетическим напитком и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (501, 'Большой Бык', 'Big Bull', '', 2, 4, 4, 'В бокал "Тумблер", или другой невысокий широкий бокал положить несколько кубиков льда и влить мексиканский кофейный ликёр Калуа и текилу.', null, 0, null, false, false, false, 0, 'tumbler');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (502, 'Пердеж Тайваньской Утки', 'Taiwan Duck Fart', '', 2, 4, 4, 'В рюмку налить треть кофейного ликера, на него наслоить треть айриш крема и на него - виски.', null, 0, null, false, true, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (504, 'Дама в Белом', 'Lady in the White', '', 2, 4, 4, 'В шейкере со льдом встряхнуть сок лимона, джин и мандариновый ликер, процедить в стакан и добавить неполную столовую ложку коньяка.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (505, 'Большой Парень', 'Big Guy', '', 2, 2, 4, 'В стакане для смешивания со льдом смешать бренди, трипл сек и сок лимона, перелить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (509, 'Бомбардино', 'Bombardino', '', 2, 2, 4, 'В бокал налить 1 часть бренди и 1 часть горячего яичного ликера (некоторые знают только Адвокат, но их много разных).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (510, 'Датское Угощение', 'Dutch Treat ', '', 2, 2, 4, 'В блендере хорошенько смешать солоко, порошок какао, ложку мёда, и по половине чайной ложки молотой корицы и ванили. Перелить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (511, 'Бомбардировка Хиросимы', 'Hiroshima Bomber', '', 2, 4, 4, 'В стопку налить три четверти трипл сек, наслоить четверть айриш крем. Сверху в середину рюмки капнуть несколько капель сиропа гренадин.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (513, 'Бомбардировщик Блицкриг', 'Blitzkrieg Bomber', '', 2, 2, 4, 'Еще один вариант приготовления - из бутылки отпить энергетический напиток, и долить в него 100 мл рома.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (514, 'Двадцатый Век', 'Twentith Century', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, сухое белое вино, белый крем де какао и сок лимона. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (517, 'Бомсайкл', 'Bombsicle', '', 2, 2, 4, 'В бокале мадлером размять малину. Добавить лёд, водку (можно малиновую) и лимонад. Перемешать и подавать, украсив ягодами малины.', null, 0, null, false, false, false, 30, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (519, 'Бонд в Таллине', 'Bond in Tallin', '', 2, 4, 4, 'В коктейльный бокал налить водку, по ложечке наслоить на него Вана Таллтин и на него наслоить сухой вермут (Мартини)
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (520, 'Девица в Темнице', 'Maiden in a Dungeon', '', 2, 4, 4, 'В рюмку капнуть лимонного сока, добавить морковный сок и перемешать. Наслоить текилу и капнуть в нее табаско.', null, 0, null, false, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (521, 'Шоколадная Обезьяна', 'Chocolate Monkey', '', 2, 4, 4, 'В шейкере встряхнуть все ингредиенты,перелить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (523, 'Зомби-Апокалипсис', 'Zombie Apocalypse', '', 2, 2, 4, 'Рецепт очень необычен, и приурочен к празднику Хэллоуин. Готовится не очень сложно, но в два этапа, и после первого этапа должно пройти минимум 10 часов.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (524, 'Крымская роза', 'Crimean Rose', '', 2, 4, 4, 'В шейкере встряхнуть мускат и розовый ликер,перелить в бокал,добавить несколько вишен и дополнить шампанским.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (525, 'Лиллет Роуз', ' Lillet Rose', '', 2, 4, 4, 'В шейкере со льдом встряхнуть розовое вино (если попадется, то <span class="component-data checklist-item-data" itemprop="ingredients"> Lillet Rose) джин и сок красных грейпфрутов.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (526, 'Кубок Стэнли', 'The Stanley Cup', '', 2, 2, 4, 'В шейкере встряхнуть джин и гренадин.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (528, 'Смерть или Слава', 'Death or Glory', '', 2, 4, 4, 'В шейкере встряхнуть компоненты,перелить в бокал Тумблер,украсить цедрой лимона и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (529, 'Смерть Девы', 'Death of a Virgin', '', 2, 2, 4, 'Водку, персиковый шнапс и сок лайма смешайте в высоком бокале. Долейте до половины апельсиновым соком, и спрайтом (или 7UP) доверху.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (563, 'Дельфин Фин', 'Dolphin Fin', '', 2, 4, 4, 'В шейкере со льдом смешать компоненты и перелить в коктейльный бокал.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1966, 'Юлия', 'Yulia', '', 2, 2, 4, 'Украсить долькой апельсина и подавать Юлию с трубочкой.', null, 0, null, false, true, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (552, 'Канатная Дорога', 'Cable Car', '', 2, 4, 4, 'Сделать сахарно коричную корочку бокалу, смочив его края соком лимона и обмакнув в сахарно-коричную смесь. В шейкере со льдом смешать пряный ром, сок лимона и сахарный сироп. Процедить в бокал, украсить коркой апельсина и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (553, 'Девушка с Марса', 'Girl From Mars', '', 2, 2, 4, 'По ободку бокала тоненькой струйкой налить гренадин, чтобы получилось кольцо, как у Марса.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (554, 'Капитанские Каникулы', 'Captain’s Vacation', '', 2, 2, 4, 'В бокал со льдом налить пряный ром, ананасовый сок и Спрайт или любую Лимонно-Лаймовую содовую.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (579, 'Зеленый Десерт', 'Green Dessert', '', 2, 2, 4, 'Бокал наполнить льдом. Влить в него водку и дынный ликер (Midori Melon Liqueur) и перемешать. Дополнить бокал Спрайтом или 7Up, уже несмешивая. И подаватвь с соломинкой.', null, 0, null, false, true, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (832, 'Красный Мексиканец', 'Red Mexican', '', 2, 4, 4, 'В рюмку налить треть клубничного ликера, наслоить на него сок лимона и на него наслоить треть текилы.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (555, 'Дези', 'Desi', '', 2, 4, 4, 'В шейкере со льдом хорошенько встряхнуть водку и сок манго, процедить и в рюмку. Добавить сверху немного взбитых сливок и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (556, 'Канкунский Закат', 'Cancun Sunset', '', 2, 4, 4, 'Украсить апельсином, мятой и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (558, 'Капитан АС', 'Captain OJ', '', 2, 2, 4, 'В стакан со льдом налить темный ром (Captain Morgan spiced rum) и дополнить его апельсиновым соком.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (559, 'Делайла', 'Delilah', '', 2, 4, 4, 'В шейкере смешать джин, трипл сек и лимонный сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (560, 'Капитан Барбосса', 'Cap&#039;n Barbossa', '', 2, 2, 4, 'В стакан со льдом налить ром и доаолнить Фантой зеленое яблоко вставить палочку корицы и украсить долькой лайма.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1872, 'Шоковая терапия', 'Shock Therapy', '', 2, 2, 4, 'В бокал для шампанского налить абсент и дополнить до краев шампанским.', null, 0, null, false, false, false, 30, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (562, 'Капитан Буччи', 'Captain Bucci', '', 2, 2, 4, 'В бокал положить четыре кубика льда. Влить в него ром и дополнить имбирным элем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (564, 'Капитан Пеппер', 'Captain Pepper', '', 2, 2, 4, 'В высоком бокале смешать 30 мл пряного рома (Captain Morgan Original spiced rum) и 180 мл Доктора Пеппера.', null, 0, null, false, false, false, 20, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (565, 'Демон', 'Demon', '', 2, 2, 4, 'Рецепт от посетителя.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (566, 'Капитанская Кровь', 'Captains Blood', '', 2, 4, 4, 'В стакане для смешивания со льдом хорошенько перемешать светлый ром, сок лайма и пару дэшей биттера. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (567, 'Демонад', 'Demonade', '', 2, 2, 4, 'В шейкере со льдом смешать спирт и лимонад, перелить в стакан, добавить гренадин и несколько капель соуса Табаско.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (568, 'Капитанское Сокровище', 'Captain&#039;s Treasure', '', 2, 4, 4, 'В рюмке смешать равные части пряного рома Captain Morgan и коричного шнапса.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (581, 'Десять Похвал', 'Dieci E Lode', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, грейпфрутовый сок и Кампари, процедить в бокал. украсить долькой лайма и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (582, 'Кардинал', 'Cardinal', '', 2, 2, 4, 'В винный бокал положить пару кубиков льда. Влить в него черносмородиновый ликер (Creme de Cassis) и дополнить бокал красным сухим вином. Украсить бокал можно ягодами и подавать.', null, 0, null, false, false, false, 60, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (742, 'Коричневый слон', 'Brown Elephant', '', 2, 2, 4, 'В 300 миллилитровый стакан положить 3 кубика льда, налить амраула крем ликер, кофейный ликер и дополнить молоком.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (569, 'Денежная Железа', 'Money Gland', '', 2, 4, 4, 'В шейкере смешать и встряхнуть все компоненты.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (570, 'Пряный Капитанский Сидр', 'Captain&#039;s Spiced Cider', '', 2, 2, 4, 'В стакан со льдом налить пряный ром и дополнить яблочным сидром.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (571, 'День Святого Патрика', 'St. Patrick&#039;s Day', '', 2, 2, 4, 'В шейкере со льдом смешать мятный ликер (Green creme de menthe), ликер зеленый Шартрёз (Green Chartreuse), ирландский виски и немного Бехеровки (или Ангостуры). Перелить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (573, 'Redneck', 'Деревеньщина', '', 2, 2, 4, 'В банку со льдом налить персиковый ликер и дополнить цитрусовым лимонадом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (574, 'Каприз', 'Caprice', '', 2, 2, 4, 'В бокал поместить коктейльную вишню, залить энергетическим напитком и шампанским.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (575, 'Дерзкая Морковь', 'Cocky Carrot', '', 2, 2, 4, 'Перелитьв стакан со льдом, украсить и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (592, 'Карлссон', 'Karlsson', '', 2, 2, 4, 'В шейкере со льдом встряхнуть банановый ликер, айриш крем и кокосовый ром. Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (577, 'Дерьмо на Траве', 'Shit on the Grass', '', 2, 4, 4, 'В рюмку налить половину зеленого мятного ликера и наслоить на него такую же часть айриш крема. Для большей реалистичности можно использовать карамельный айриш крем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (596, 'Катенька', 'Katinka', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, абрикосовый бренди и сок лайма, процедить в коктейльный бокал и украсить листочками мяты.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (623, 'Джин Клубничный Краш', 'Gin Strawberry Crush', '', 2, 2, 4, 'В стакан со льдом добавть нарезанную клубнику, влить джин и дополнить Спрайтом или 7Up.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (578, 'Синий Караченцов', 'Dark blue Karachentsov', '', 2, 4, 4, 'В рюмку налить голубой Кюрасао, наслоить водку и добавить несколько капель Табаско. Коктейль готов!
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (583, 'Детокс', 'Detox', '', 2, 2, 4, 'В блендере взбить два очищенных яблока, половину огурца, две веточки сельдерея, четвертинку очищенного лимона, несколько листочков мяты и немного воды до состояния смузи, перелить в бокал и подавать, с ложечкой и соломинкой.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (584, 'Карелия', 'Karelia', '', 2, 2, 4, 'Рецепт от посетителя:
