INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (587, 'Джек и Кока', 'Jack and Coke', '', 2, 2, 4, 'В Высокий стакан заполненный льдом влить виски (предпочтительно Джек Дениелс) и колу (предпочтительно Кока-Колу)', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (588, 'Карибу Лу', 'Caribou Lou', '', 2, 2, 4, 'Название ирецепт взяты изпесни рэпера <strong>TechN9ne</strong>&mdash; <em>CaribouLou</em>, где поётся &hellip;151 rum, pineapple juice, and malibu, caribou, get them all numb&hellip; Впесне неуказана пропорция, поэтому можете фантазировать, нонезабывайте, что смешиваете два крепких рома, разбавляете ананасовым соком ипереливаете встакансольдом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (589, 'Джек Леммон ДМН', 'Jack Lemmon M.D.', '', 2, 2, 4, 'В кружку Мэйсона со льдом налить бурбона (Jack Daniel''s Tennessee whiskey) отжать поллимона, и дополнить Доктором Пеппером. Украсить долькой лимона.', null, 0, null, false, false, false, 0, 'mayson');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (591, 'Джек Роуз', 'Jack Rose', '', 2, 4, 4, 'Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (595, 'Джи 4', 'G4', '', 2, 4, 4, 'В рюмке на амаретто наслоить айриш крем в равных частях.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (666, 'Кнопка', 'Popper', '', 2, 4, 4, 'Перед употребелением накрыть рюмку ладонью и хлопнуть об стол.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (598, 'Катино Совершеннолетие! ', 'Katy&#039;s Eighteen!', '', 2, 2, 4, 'В большой чаше (чаше для крюшона) смешать три винных бокала водки, половину бокала рома, пять винных бокалов ананасового сока и восемь бокалов апельсинового сока. В конце добавить бокал шампанского.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (599, 'Джи Стринги', 'G-String', '', 2, 4, 4, 'В шейкере со льдом встряхнуть 45 мл водки и по 7 мл сливок и темного крем де какао. Процедить в бокал и подавть.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (600, 'Катринтини', 'Kathryntini', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, малиновый ликер, кокосовый ликёр (можно заменить на кокосовый ром), перелить в бокал и добавить Спрайт и ли 7Up и украсить спиралькой из цедры лимона.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (601, 'Джимми Два Раза', 'Jimmy Two Times', '', 2, 4, 4, 'Смешать равные части бурбона (Jim Beam bourbon whiskey) и черной самбуки. Разлить по рюмкам.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (602, 'Катюша', 'Katiusha', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, абрикосовый бренди, бальзам Ванна Таллин и сок лимона.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (603, 'Двойной Шот из Ада', 'Double Shot From Hell', '', 2, 4, 4, 'В шейкере со льдом встряхнуть текилу, мандариновую водку и джин. Процедить в большую рюмку (двойной шот) и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (606, 'Кафе Парижа', 'Café de Paris', '', 2, 4, 4, 'В шейкере со льдом хорошенько протрясти джин, абсент, сливки и яичный белок. Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (607, 'Джин Балалайка', 'Gin Balalajka', '', 2, 2, 4, 'В шейкере со льдом встряхнуть джин и апельсиновый сок, перелить в стакан со льдом и дополнить тоником. (Если повезет найти, то лучше всего использовать Schweppes Russian Water)', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (608, 'Кафе Романо', 'Cafe Romano', '', 2, 4, 4, 'В шейкере, наполовину заполненном льдом, встряхнуть самбуку, кофейный ликер и сливки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (609, 'Джин Джин', 'Gin Gin', '', 2, 4, 4, 'В бокал налить джин и дополнить имбирным элем (ginger ale schweppes)', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (610, 'Кафе Савой', 'Cafe Savoy', '', 2, 2, 4, 'В чашку налить горячий кофе, молоко (по вкусу) бренди и трипл сек.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (611, 'Джин и Грех', 'Gin and Sin', '', 2, 4, 4, 'В шейкере, на половину заполненном льдом, встряхнуть джин, апельсиновый сок, сок лимона и чайную ложку гренадина, процедить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (613, 'Джин и Клюква', 'Gin &amp; Cranberry', '', 2, 2, 4, 'Украсить долькой лимона и подавтаь.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (614, 'КГБ # 2', 'KGB', '', 2, 4, 4, 'В шейкере с колотым льдом встряхните кирш, джин и абрикосовый бренди.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (615, 'Джин и Сок', 'Gin and Juice', '', 2, 2, 4, 'Можно украсить долькой лайма.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (616, 'Кейп Кодер', 'Cape Codder', '', 2, 2, 4, 'Подавать!', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (617, 'Шафрановый Джин Тоник', 'Saffron Gin &amp; Tonic', '', 2, 2, 4, 'В шейкере мадлером размять шепотку шафрана. Добавить лёд, джин и тоник. Встряхнуть и процедить в бокал с несколькими кубиками льда. Добавить дольку лайма и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (619, 'Джин и Это', 'Gin and It', '', 2, 4, 4, 'В коктейльный бокал налить джин и добавить в него вермут.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (622, 'Кейп Коддер', 'Capecodder', '', 2, 2, 4, 'Иногда водку заменяют светлым ромом и добавляют сок лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (624, 'Кенгуру', 'Kangaroo', '', 2, 4, 4, 'В шейкере на половину заполненном льдом встряхнуть водку и вермут, процедить в коктейльный бокал, украсить спиралькой из лимона и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (625, 'Джин Лимонад', 'Gin Lemonade', '', 2, 2, 4, 'В стакане со льдом смешать джин, сахарный сироп, сок лимона, и дополнить содовой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (627, 'Джин Рикки', 'Gin Rickey', '', 2, 2, 4, 'В бокал со льдом налить джин, отжать сок половинки лайма и дополнить содовой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (629, 'Джин Серфер', 'Gin Surfer', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин и голубой Кюрасао. Процедить в рюмку и подавать.', null, 0, null, false, false, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (630, 'Киба', 'Kiba', '', 2, 2, 4, 'В стакане со льдом смешать банановый и вишневый соки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (631, 'Джин Тодди', 'Gin Toddy', '', 2, 2, 4, 'В бокал налить воду и сок лимона. размешать в ней пол ложки сахарной пудры, долить джин и, добавив кубик льда, подавать с палочкой корицы', null, 0, null, false, false, false, 0, 'brandy');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (634, 'Клубничный Кир', 'Strawberry Kir', '', 2, 2, 4, 'В винный бокал налить одну часть клубничного ликера и пять частей сухого белого вина.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (635, 'Джон Дир', 'John Deere', '', 2, 4, 4, 'В рюмку налить яблочный шнапс и наслоить на него Егермейстер.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (636, 'Китайский Дракон', 'Chinese Dragon', '', 2, 4, 4, 'Вбокал с гренадином аккуратно наслоить содержиме шейкера и добавить немного содовой, не нарушая целостности слоев.', null, 0, null, false, true, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (637, 'Джон Коллинз', 'John Collins', '', 2, 2, 4, 'В бокал со льдом налить джин, сок лимона, сахарный сироп, Ангостуру и содовую, немного перемешать. Украсить коктейльной вишней, кружком лимона и подавать с соломинкой.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (638, 'Кладбище разума', 'Cemetery Of Mind', '', 2, 4, 4, 'Перед пьющим ставится бокал с текилой и ему выдается соломинка.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (639, 'Джонатан Стрендж', 'Jonathan Strange', '', 2, 2, 4, 'Налить половину бокала сухого шампанского и аккуратно, по стеночке дополнить его темным пивом (Guinness stout)   Джонатан Стрендж (Jonathan Strange) - один из героев фантастической книги Сюзанны Кларк про магов в Англиии.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1138, 'Погребальная Услуга', 'Funeral Service', '', 2, 4, 4, 'В рюмку налить кофейный ликер, на него наслоить самбуку, а на нее наслоит абсент.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (640, 'Кларанс', 'Clarence', '', 2, 2, 4, 'В шейкере со льдом встряхнуть равные части кокосового рома, гренадина, трипл сек. Процедить в коньячный бокал и подавать.', null, 0, null, false, false, false, 40, 'brandy');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (641, 'Абрикосово-Мятный Джулеп', 'Apricot Mint Julep', '', 2, 2, 4, 'В блендере взбить три очищенных от кожици и косточек абрикоса, листочек мяты, сок четвертинки лайма, ложку сахара и 4 кубика льда.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (642, 'Кленовый Антигель', 'Maple&#039;s Antigel', '', 2, 2, 4, 'В кружку налить кипяток, добавить кленовый сироп и джин. Размешать и подавать.', null, 0, null, false, false, false, 60, 'bowl');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (643, 'Джулиана Блю', 'Juliana Blue', '', 2, 2, 4, 'В блендер поместить несколько кубиков льда, долить ананасовый сок, джин, трипл сек, голубой Кюрасао, кокосовый крем и взбить до консистенции мокрого снега.
