INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (720, 'Компот Первоклашка', 'Compote First-grader', '', 2, 2, 4, 'В кастрюлю поместить 200 г смородины, 200 г вишни и 200 г малины. Залить 2 литрами кипятка, добавить сахар по вкусу и довести до кипения. Охладить до комнатной температуры и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (721, 'Супер Дьявол', 'Super Devil', '', 2, 2, 4, 'В шейкере со льдом встряхнуть крепкий ром (Bacardi 151), ежевичный бренди и гренадин. Перелить в высокий бокал. Долить тоник, оставив место для коричного шнапса.
