INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (724, 'Константин', 'Constantine', '', 2, 2, 4, 'В стакан Маргарита наложить колотого льда, налить водку, клюквенный сок, яблочный шнапс, перемешать и добавить содовой.
