INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (728, 'Конфета Тщеславия', 'Bonbon of the Vanities', '', 2, 2, 4, 'В шейкер налить водку, банановый ликер, персиковый ликер и гренадин. Наполнить на 1/3 льдом и встряхнуть в течении 3-5 секунд. Процедить в бокал с тремя кубиками льда. Добавить апельсиновый сок, стараясь не сильно смешивать слои и подавать, украсив долькой апельсина.', null, 0, null, false, true, false, 60, 'highball');
