INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (785, 'Жидкий Крэк', 'Liquid Crack', '', 2, 4, 4, 'В рюмке смешать равные части крепкого рома и спирта.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (786, 'Кофе по-ирландски', 'Irish Coffee', '', 2, 2, 4, 'В кофейную кружку налить виски, кофе и положить ложечку коричневого сахара. Перемешать, затем сверху наслоить холодные сливки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (787, 'Жидкий Опиум', 'Liquid Opium', '', 2, 4, 4, 'В старомодном стакане смешать Бехеровку, гренадин и спрайт, а в рюмке - абсент с самбукой. Содержимое рюмки поджечь и через 5 секунд перелить в стакан с подготовленной смесью спрайта и бехеровки. Накрыть бердекелем (подставкой под пиво) или ладонью. ударить об стойку и залпом выпить.  Не разбейте посуду!', null, 0, null, true, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (788, 'Кофейный Блюз', 'Coffee Blues', '', 2, 4, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (790, 'Игуана', 'Iguana', '', 2, 4, 4, 'В шейкере со льдом встряхнуть 2 части водки, одну часть текилы и одну часть кофейного ликера. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (791, 'Жидкий Электрошок', 'Liquid Electroshock', '', 2, 4, 4, 'В шейкер положить несколько свежих вишен, размять и засыпать льдом. Долить водку, клюквенный и апельсиновый сок, встряхнуть и процедить в коктейльный бокал.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (792, 'Кофейный Поцелуй', 'Coffee Kiss', '', 2, 4, 4, 'В шейкере со льдом встряхнуть кофейный ликер и процедить в рюмку, плеснуть сливки и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (793, 'Журавли', 'Cranes', '', 2, 4, 4, 'Охладить стакан, пиво и абсент, чтобы коктейль не пенился.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (794, 'Кочегар', 'Stoker', '', 2, 2, 4, 'В бокал налить виски и дополнить светлым пивом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (795, 'Жуткий Баг', 'Terrible bug', '', 2, 2, 4, 'В бокале смешать равные части сливогого, апельсинового, ананасового, грейпфрутогого сока и водки.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (797, 'ЗаЗа', 'ZaZa', '', 2, 4, 4, 'Вбокал плеснуть апельсинового биттера, за тем налить джин и красный вермут.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (799, 'Забирающий Душу', 'Soul Taker', '', 2, 4, 4, 'В стакане со льдом смешать равные части водки, текилы и амаретто. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (800, 'Крановщица', 'Cranewoman', '', 2, 2, 4, 'Налить компоненты в стакан со льдом и украсить кружком из лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (801, 'Черная Зависть', 'Kali&#039;s Envy', '', 2, 2, 4, 'Кали - (санскр. черная) - тёмная и яростная ипостась Парвати, темная Шакти и разрушительный аспект Шивы.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (802, 'Крантини', 'Crantini', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку и клюквенный ликер. Процедить в бокал и подавать, украсив долькой лайма.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (803, 'Ром на Завтрак', 'Breakfast Rum', '', 2, 2, 4, 'Набрать стакан льда и пересыпать в шейкер. Влить в него ром, сок лимона и кленовый сироп. Встряхнуть и перелить обратно в стакан.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (804, 'Красивая Мексика', 'México Lindo', '', 2, 4, 4, 'Встряхнуть и процедить в бокал, украсить коктейльной вишней и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (805, 'Завтрак Обезьяны', 'Monkey Brakefast', '', 2, 2, 4, 'В блендере с несколькими кубиками льда пробить айриш крем, кокосовый ликер, банановый ликер, молоко и половинка банана. Перелить в бокал, украсить бананом и подавать с трубочкой.', null, 0, null, false, false, false, 60, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (809, 'Задница', 'Ass', '', 2, 4, 4, 'Налить рюмку водки и плеснуть в нее немного арбузного шнапса.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (810, 'Кулер с Красным Вином', 'Red Wine Cooler', '', 2, 2, 4, 'В бокал со льдом налить равные части цитрусовой содовой (Спрайт, 7Up или Маунтин Дью), имбирного эля и две части красного вина.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (811, 'Зайка', 'Honey Bunny', '', 2, 2, 4, 'Встакан со льдом налить морковный сок, апельсиновый сок и гренадин, перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (812, 'Красное Пиво', 'Red Beer', '', 2, 2, 4, 'В пивной стакан налить светлое пиво, добавить томатный сок и оливку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (814, 'Красное Яблоко', 'Red Apple', '', 2, 4, 4, 'В шейкере со льдом встряхнуть бурбон, кислый яблочный ликер и клюквенный сок. Процедить в бокал и подпавать. В качестве украшения можно использовать коктейльную вишенку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (815, 'Замбито', 'Zambito', '', 2, 4, 4, 'В шейкере со льдом встряхнуть бренди, сок лайма и сахарный сироп. Процедить в бокал, добавить три кофейных зернышка и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (816, 'Красная Кожа', 'Red Skin', '', 2, 4, 4, 'Процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (818, 'Красный', 'Red Drink', '', 2, 2, 4, 'В блендере пробить до состояния смузи ром, банановый ликер, 16 замороженных ягод клубники, два банана и перелить в бокалы.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (820, 'Красный Глаз', 'Red Eye', '', 2, 2, 4, 'В пивной стакан налить холодный томатный сок, затем пиво, и потом выпустить туда желток от куриного яйца (или целое яйцо, по вкусу). Подавать не перемешивая.', null, 0, null, false, true, false, 0, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (821, 'Замороженный Дайкири', 'Frozen Daiquiri', '', 2, 4, 4, 'В блендер поместить лёд, светлый ром, сок лайма и сахарный сироп. Взбить до однородной консистенции и перелить в бокал. Украсить долькой лайма и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (822, 'Красный Горячий', 'Red Hot', '', 2, 4, 4, 'В рюмке смешать коричный шнапс и Егермейстер.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (823, 'Зарисованный Красным', 'Red Draw', '', 2, 2, 4, 'В стакан светлого пива влить немного томатного сока.', null, 0, null, false, false, false, 50, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (824, 'Красный Джин', 'Red Gin', '', 2, 4, 4, 'В стакане для смешивания со льдом встряхнуть джин и ложку вишневого ликера. ПРоцедить в бокал и подавать, украсив коктейльной вишней.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (825, 'Зарядник для Слона', 'Elephant Charger', '', 2, 2, 4, 'В блендер поместить один очищенный от кожуры мягкий банан и один персик без косточки, долить апельсиновый сок, молоко, добавить мороженое и 8 ягодок малины. Взбить все до гомогенного состояния, перелить в стакан и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (827, 'Засос', 'Love Bite', '', 2, 4, 4, 'В бокал налить вишневый ликер, наслоить на него апельсиновый ликер (Перфе Амур - Parfait Amour orange liqueur) и на него наслоить сливки.', null, 0, null, false, true, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (828, 'Красный или Мертвый', 'Red or Dead', '', 2, 2, 4, 'В стакане со льдом смешать ликер Саузен Комфорт, Кампари, клюквенный сок и сок лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (829, 'Засранец', 'Asshole', '', 2, 4, 4, 'Наслейте в шейкер со сльдом хорошо охлажденные компоненты, втряхните, перелейте в стопку и незамедлительно выпейте!', null, 0, null, false, false, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (830, 'Красный Мародер', 'Red Marauder', '', 2, 4, 4, 'В шейкере со льдом встряхнуть коньяк, малиновый ликер, клюквенный сок и сок лайма. процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (831, 'Застенчивый Банан', 'Timid Banana', '', 2, 2, 4, 'В блендере взбить банан, сок, малиновый сироп и мороженое, перелить в бокал и украсить кружком банана.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (834, 'Красный ноготь', 'Red Nail', '', 2, 4, 4, 'В бокал для коктейлей влить водку и Кампари, положить 4 кубика льда, подождать 7 минут и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (835, 'Зведный Коктейль', 'Star Cocktail', '', 2, 4, 4, 'В шейкере со льдом встряхнуть кальвадос, красный сладкий вермут и ангостуру, процедить в бокал и подавать, украсив цедрой апельсина.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (837, 'Звезды Далласа', 'Dallas Stars', '', 2, 4, 4, 'В рюмку налить коричный шнапс и наслоить на него зеленый мятный ликер.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (839, 'Зверский Аустер', 'Brutal Oyster', '', 2, 4, 4, 'В большую широкую рюмку налить масло и распространить его по стенкам ровным слоем. Налить водку, добавить яичный желток, соль, соус Табаско, по четверти ложки красного и черного перца.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (840, 'Красно-Рубиновый', 'Red Ruby', '', 2, 4, 4, 'Стакан для смешивания на половину заполнить льдом. Влить в него джин, шери бренди и сухой вермут. Перемешать и процедить в бокал. Подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (841, 'Зеленая Вдова', 'Green Widow', '', 2, 4, 4, 'В шейкере со льдом встряхнуть голубой Кюрасао и апельсиновый сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (844, 'Красный Ураган ', '', '', 2, 2, 4, 'В стакан со льдом влить все компоненты, перемешать и подвать.', null, 0, null, false, false, false, 40, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (846, 'Красный Штат Столи', 'Stoli Red State', '', 2, 4, 4, 'Коктейль можете смешать в шейкере (все компоненты за исключением содовой, её плесните в последнюю очередь). Можете его приготовить on the rocks (то есть заполнить бокал льдом и последовательно влить компоненты).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (847, 'Зеленая Лягушка', 'Green Frog', '', 2, 2, 4, 'В стакан со льдом налить банановый ликер, Спрайт или 7Up и добавт сок лайма.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (849, 'Зеленая Фея', 'Green Fairy', '', 2, 4, 4, 'в рюмку налить по трети каждого из ингредиентов и небрежно подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (850, 'Крепкое объятие', 'Strong Embrace', '', 2, 4, 4, 'В шейкере со льдом встряхнуть абсент, джин и виски, перелить в большую стопку.', null, 0, null, false, false, false, 30, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (851, 'Зеленые Глаза', 'Green Eyes', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку голубой Кюрасао и апельсиновый сок, процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (853, 'Зеленая Листва', 'Green Leaves', '', 2, 2, 4, 'В бокал положить несколько мятных листочков и налить мятный сироп. Размять барной ложечкой и добавить ложку колотого льда. Дополнить бокал тоником и подавать.', null, 0, null, false, false, false, 0, 'highball');
