INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (856, 'Крестница', 'God Daughter', '', 2, 2, 4, 'В шейкере со льдом встряхнуть самбуку, амаретто, сливки и гренадин. Процедить в бокал, украсить клубникой, листочком мяты и посыпать тертым шоколадом.', null, 0, null, false, false, false, 60, 'margarita');
