INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (864, 'Кричащий Викинг', 'Screaming Viking', '', 2, 2, 4, 'В бокал со льдом налить водку, сухой вермут, сок лайма, добавить натертый на мелкой терке ломтик огурца, перемешать палочкой сельдерея и подавтать.
