INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (867, 'Зеленый Испанец', 'Green Spanish', '', 2, 4, 4, 'В рюмку наливаем абсент, на него наслаиваем сок лайма и на него - текилу.', null, 0, null, false, true, false, 0, 'shot');
