INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (871, 'Зеленый мексиканец', 'Green Mexican', '', 2, 2, 4, 'В стакан со льдом налить текилу, дынный ликер, и влить сок лимона с растворенной в нем пудрой. Подавать, украсив кружком из апельсина и вишенкой.', null, 0, null, false, false, false, 0, 'collins');
