INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (875, 'Зеленый Русский', 'Green Russian', '', 2, 2, 4, 'В стакан со льдом налить дынный ликер и водку. Помешивая, добавить сливки, до получения приятного на Ваш взгляд цвета.', null, 0, null, false, false, false, 0, 'highball');
