INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (876, 'Кроваво-Апельсиновая Водка', 'Bloody Orange Vodka', '', 2, 4, 4, 'Красные сицилийские апельсины нарезать на кружочки, поместить в банку и залить водкой. Закрыть герметичной крышкой и дать настояться в течение недели в темном прохладном месте.
