INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (879, 'Зеленый Яд', 'Green Poison', '', 2, 4, 4, 'В шейкере со льдом встряхнуть зеленый Шартрёз, джин и сок лимона, процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 20, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (880, 'Кроваво Апельсиновый Кампари', 'Blood Orange Campari', '', 2, 2, 4, 'Для приготовления коктейля нужны апельсины сорта Королек - красные внутри, такие часто продаются в наших магазинах, в основном привозят их с Сицилии.  Красный апельсиновый сок можно купить и в пакетах.  Понадобится выжать сок из расчёта 4 апельсина на бутылку Шампанского.  Разлить сок по бокалам, примерно по 2 ложки в каждый, потом долить Кампари. Затем дополнить бокалы охлажденным шампанским.  Украсить бокалы кружочками из апельсина и подавать.', null, 0, null, false, false, false, 50, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (881, 'Землетрясение', 'Earthquake', '', 2, 4, 4, 'В шейкере со льдом встряхнуть бурбон, пастис и джин. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
