INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (885, 'Зимний Зачет', 'Winter Offset', '', 2, 2, 4, 'В большой бокал для шампанского налить водку, бальзам Бана Таллин и дополнить шампанским.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (886, 'Кровавый Мозг Шутер', 'Bloody Brain Shooter', '', 2, 4, 4, 'В рюмку налить охлажденную водку и сок лимона или лайма. Взять соломинку и опустить ее в бутылку со сливочным ликером так, чтобы в нее набралось немного жидкости, заткнуть пальцем верхнее отверстие соломинки и перенести ликер в рюмку, где аккуратно выпустить содержимое, не перемешивая его с водкой. проделать эту процедуру несколько раз, до достижения визуального эффекта наличия мозгового вещества в рюмке. капнуть 1-2 капли гренадина и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (887, 'Злая Принцесса', 'Evil Princess', '', 2, 2, 4, 'В стакан со лидом налить гренадин, виноградный и яблочный соки, ванильный сироп и сок лимона, перемешать и подавать украсив долькой лимона.', null, 0, null, false, false, false, 60, 'collins');
