INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (911, 'Золотой Стрелок', 'Golden Shooter', '', 2, 4, 4, 'В рюмку сауэр налить ликер Драмбуи, на него наслоить амарула крем и на него наслоить водку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (913, 'Золушка', 'Cinderella', '', 2, 2, 4, 'В стакане для смешивания размять три четверти лайма и треть лимона. Добавить лёд, апельсиновый сок и ананасовый сок. Перемешать и процедить в бокал со льдом. Украсить кружком из лайма и подавать.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (914, 'Крыло Ангела', 'Angel&#039;s Wing', '', 2, 4, 4, 'Присыпать тертым шоколадом и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (916, 'Крылья Дьявола', 'The Devil&#039;s Wings', '', 2, 2, 4, 'Плеснуть коричного шнапса и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (917, 'Зорбатини', 'Zorbatini', '', 2, 4, 4, 'В коктейльном бокале смешать охлажденные водку и анисовый ликер Узо. Добавить оливку наколотую на зубочистку и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (965, 'Истребитель', 'Fighter', '', 2, 2, 4, 'Вбокал со льдом налить перцовую водку и клюквенный сок. Перемешать и подавтаь.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (921, 'Зеленая Игуана', 'Green Iguana', '', 2, 2, 4, 'В шейкересо льдом встряхнуть текилу, дынный ликер, сахарный сироп и сок лайма. Процедить в бокал со льдом и подавать.', null, 0, null, false, false, false, 0, 'margarita');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (923, 'Извержение', 'Eruption', '', 2, 4, 4, 'Стакан для смешивания до половины заполнить льдом. Влить в него канадский виски и черносмородиновый ликер (Creme de Cassis) Перемешать, процедить в коктейльный бокал. Украсить коктейльной вишней и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (968, 'Лава Шот', 'Lava Shot', '', 2, 4, 4, 'Налить в рюмку все три компонента и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (924, 'Крэйзи Эрика', 'Crazy Erika', '', 2, 2, 4, 'В стакан налить все ингредиенты, кроме газировки, размешать до растворения сахара, затем добавить лед и спрайт или 7up.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (925, 'Ванильный Иисус', 'Vanilla Jesus', '', 2, 4, 4, 'Процедить в рюмку и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (926, 'Крэйзи Джус', 'Crazy Juice', '', 2, 4, 4, 'Смешать водку и гранатовый сок. Перелить в коктейльный бокал с несколькими кубиками льда.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (927, 'Икс Игрек Зэт', 'XYZ', '', 2, 4, 4, 'В шейкере со льдом смешать компоненты, перелить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (928, 'Ксантиппа', 'Xantippe', '', 2, 4, 4, 'В шейкере со льдом встряхнуть две части водки и по одной части шерри бренди и желтого Шартрёза. Процедить в бокал и поадвать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (929, 'Илиада', 'Illiad', '', 2, 2, 4, 'Подавать, пока клубника медленно оседает на дно.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (930, 'Ксю Ксю Роял', 'XUXU Royal', '', 2, 2, 4, 'В бокал для шампанского налить клубничный ликер (XuXu) и дополнить бокал шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (931, 'Именинный Пирог Мартини', 'Birthday Cake Martini', '', 2, 4, 4, 'Будьте осторожный при употреблении данного коктейля. Если гореть будет долго, то края бокала нагреются и он может лопнуть.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (932, 'КсюКсюБана', 'XuXuBana', '', 2, 2, 4, 'В высоки стакан положить пару кубиков льда и налить банановый нектар. Аккуратно, стараясь не перемешивать влить в в него клубничный ликер. Украсить ягодкой клубники и подавать с двумя соломинками.', null, 0, null, false, true, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (933, 'Девочка Индиго', 'Indigo Girl', '', 2, 4, 4, 'Процедить в охлажденный бокал и, украсив маленькой долькой лайма, подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (934, 'Куантрополитен', 'Cointreaupolitan', '', 2, 4, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (936, 'Кирш Куба Либре', 'Kirsch Cuba Libre', '', 2, 2, 4, 'В высокий бокал положить льда, влить кирш, колу, и отжать сок половины лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (937, 'Индийский Дикий Осел', 'Wild Ass Indian', '', 2, 4, 4, 'Смешать бутылку водки, бутылку рома и бутылку виски.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (938, 'Кубинская Мэри', 'Cuban Mary', '', 2, 2, 4, 'В стакане со льдом смешать ром, сок половины лайма, соус Табаско, томатный сок, щепотку соли. украсить веточкой сельдерея и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (939, 'Индийский Лоск', 'Indian Lustre', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, голубой кюрасао и сливки, процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (940, 'Кубинский Коктейль № 1', 'Cuban Cocktail No.1', '', 2, 4, 4, 'В шейкере со льдом встряхнуть ложечку сахарной пудры, сок четверти лимона и 90 мл рома. Процедить в бокал, украсить долькой лайма и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (941, 'Индульгенция', 'Indulgence', '', 2, 4, 4, 'В рюмку налить кофейный ликер, наслоить на него Амаретто и сверху наслоить Айриш крем.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (942, 'Кубинский Винт', 'Cuban Screw', '', 2, 2, 4, 'В стакан со льдом налить ром и апельсиновый сок. Перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (943, 'Интим Бофорте', 'Intim Bofort', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (947, 'Холодная Ирландская Зима', 'Cold Irish Winter', '', 2, 2, 4, 'В чашку налить горячий крепкий кофе, виски и кофейный ликер, сверху в чашку добавить взбитые сливки.', null, 0, null, false, true, false, 0, 'cup');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (949, 'Ирландская Корова', '', '', 2, 2, 4, 'Смешать Айриш Крем с Молоком и перелить в бокал для Ирландского кофе (можно использовать обычную кофейную чашечку). По желанию, сверху можно натереть немного мускатного ореха, для придания более пряного и острого вкуса.', null, 0, null, false, false, false, 0, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (951, 'Ирландская Роза', 'Irish Rose', '', 2, 2, 4, 'В стакан наслить сок лимона, вишневый сок и дополнить содовой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (952, 'Кукакарача', 'Cucacaracha', '', 2, 4, 4, 'В большую рюмку налить в равных частях водку, текилу, кофейный ликер и последней содовую.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (953, 'Ирландские Глаза', 'Irish Eyes', '', 2, 4, 4, 'В стакане со льдом смешиваем ирландский виски, мятный ликер и сливки, процеживаем в коктейльный бокал и украшаем его коктейльной вишней.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (954, 'Клюквенно-Лаймовый Кулер', '', '', 2, 2, 4, 'Влить в него цитрусовую содовую на три четверти, отжать четверть лайма и дополнить клюквенным соком.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (955, 'Ирландский Пират', 'Irish Pirate', '', 2, 2, 4, 'В блендер налить молоко, добавить несколько листочков мяты, мороженое и тертый шоколад (можно использовать фабричное мороженое с тертым шоколадом) Взбить до вспенивания и перелить в бокал. Украсить листочком мяты и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (956, 'Куллоден', 'Culloden', '', 2, 2, 4, 'В бокал налить виски и дополнить его охлажденным шампанским.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (957, 'Ирландский Элвис', 'Irish Elvis', '', 2, 2, 4, 'В Пивной бокал налить бурбон (Jack Daniels) хорошенько плеснуть колы и дополнить темным пивом (Guinness) .
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (958, 'Кум Шот', 'Cum Shot', '', 2, 4, 4, 'В рюмке смешать ирисовый шнапс и айриш крем, сверху добавить взбитые сливки и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (960, 'Куперстаун', 'Cooperstown', '', 2, 4, 4, 'В шейкере со льдом встряхнуть сладкий красный вермут, сухой белый вермут и джин, процедить в коктейльный бокал, добавить веточку мяты и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (961, 'Искушение', 'Temptation', '', 2, 4, 4, 'Вблендере с ложкой колотого льда пробить кружок ананаса, абсент и персиковый сок. Процедить в бокал, украсить долькой ананаса и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (962, 'Датский Кураж', 'Dutch Courage', '', 2, 2, 4, 'В шейкере со льдом встряхнуть яичный ликер, джин, яблочный сок и сок лимона. Процедить в стакан со льдом, украсить долькой лимона и подавать.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (964, 'Кэти', 'Katie', '', 2, 2, 4, 'В большой винный бокал положить колотый лёд, налить ром, персиковый шнапс и лимонад. За тем добавить дынный ликер, не перемешивая. Гренадин налить по стеночке, чтобы остались потеки, если получится.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (990, 'Леди В Красном', 'The Lady In Red', '', 2, 4, 4, 'Налить в рюмку персиковый шнапс, мятный ликер, водку и сверху сироп гренадин, дать постоять полминуты и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (970, 'Арканзасская Лавина', 'Arkansas Avalanche', '', 2, 2, 4, 'В шейкере, на половину заполненном льдом встряхнуть компоненты и перелить в стакан.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (972, 'Лавовая Лампа', 'Lava Lamp', '', 2, 2, 4, 'В коньячный бокал налить Маунтин Дью и влить в него из рюмки коричный шнапс.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (973, 'Итальянский Свадебный Торт Мартини', 'Italian Wedding  Cake Martini', '', 2, 4, 4, 'Вшейкер на половину заполненный льдом влить водку, какао ликер (Creme de cacao) и грейпфрутовый сок. хорошенько встряхнуть и процедить в бокал.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (974, 'Лайм Тоник', 'Lime Tonic', '', 2, 2, 4, 'В стакане со льдом смешать сок лайма и тоник. Украсить долькой лайма и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (975, 'Итальянский Серфер', 'Italian Surfer', '', 2, 2, 4, 'В шейкере с льдом встряхнуть амаретто, кокосовый ром и ананасовый сок, перелит в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (976, 'Лаймовая Зажигалка', 'Lime Lighter', '', 2, 2, 4, 'В шейкере со льдом встряхнуть темный ром, трипл сек, сок лайма свежевыжатый и сок лайма (Rose''s lime juice) - 4 мл. Перелить процедить в коктейльный бокал или перелить в бокал со льдом и подавать.', null, 0, null, false, false, false, 40, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (977, 'Слепой Итальянец', 'Italian Blind', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, Егермейстер, сок лимона, сахарный песок и кофе (можно эспрессо. Если кофе вареный, то процеживать коктейль в бокал нужно через фильтр для кофе.)
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (979, 'Ищейка', 'Bloodhound', '', 2, 4, 4, 'Процедить в бокал и подавать, украсив клубникой.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (980, 'Лапландия', 'Laplandia', '', 2, 2, 4, 'В блендер поместить молоко, пол стакана сахарного песка и 1-2 яичных желтка. Взбить до растворения сахара и образования обильной пены. Добавить водку и еще с полминуты повзбивать. Разлить по бокалам со льдом и подавать с соломинкой.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (981, 'Июньский Жук', 'June Bug', '', 2, 2, 4, 'Джун баг - готов!
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (982, 'Лас Вегас', 'Las Vegas', '', 2, 2, 4, 'В бокале, на половину наполненном колотым льдом, смешать золотую текилу, апельсиновый сок, ананасовый сок, кокосовый крем и взбитые сливки. Подавать украсив долькой ананаса и с соломинкой.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (983, 'Йогуртовый Кулер', 'Yoghurt Cooler', '', 2, 2, 4, 'В блендере взбить до однородной консистенции равные части йогурта, льда и очищенных фруктов, на ваш вкус.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (984, 'Ласковый Убийца', 'The Tender Murderer', '', 2, 4, 4, 'В рюмку налить равные части мятного ликера, абсента и трипл сека.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (985, 'Кабалло', 'Сaballo', '', 2, 2, 4, 'Перемешать и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (986, 'Томатный Ласси', 'Tomato Lassi', '', 2, 2, 4, 'В блендере взбить йогурт, томатный сок, воду соль и сельдерей.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (987, 'Кабо', 'Cabo', '', 2, 4, 4, 'В шейкере со льдом встряхнуть компоненты и перелить в коктейльный бокал.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (988, 'Легкая Жизнь', 'Bed of Roses', '', 2, 4, 4, 'В шейкере со льдом встряхнуть Егермейстер, сок лимона, сок лайма и гренадин. Процедить в бокал, украсить цедрой лимона и вишенкой.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (991, 'Кагуяма', 'Kagayama', '', 2, 2, 4, 'Свежевыжатый апельсиновый сок смешать с сахарным сиропом, Гренадином и светлым ромом, подогреть и перелить в бокал для ирландского кофе, украсить апельсином и вишней.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (992, 'Леди Асти', 'Lady Asti', '', 2, 2, 4, 'Рецепт от послетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (993, 'Казанова', 'Casanova', '', 2, 4, 4, 'В шейкере с большим количеством льда встряхнуть бурбон, итальянское красное сладкое вино  Marsala dolce, кофейный ликер и жирные сливки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (994, 'Леди Коктейль', 'Ladies Cocktail', '', 2, 4, 4, 'Встакан со льдом налить виски, анисовый ликер, капнуть биттер, перемешать, процедить в бокал, украсить ананасом и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (995, 'Казантип', 'Kazantip', '', 2, 4, 4, 'В рюмку налить гренадин, на него по барной ложечке наслоить самбуку, на нее айриш крем и сверху - абсент. Перед подачей поджечь.', null, 0, null, true, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (996, 'Леди Челло', 'Lady Cello', '', 2, 2, 4, 'В шейкере со льдом встряхнуть лимончелло, ром, сок лимона и гренадин.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (997, 'Казачий', 'Cossack’s', '', 2, 4, 4, 'В стакане для смешивания со льдом встряхнуть две части джина, и по одной части Кюммеля и сока лимона. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (998, 'Ледокол', 'Ice Breaker', '', 2, 2, 4, 'В блендере с двумя ложками колотого льда взбить все компоненты, перелить в винный бокал и подавать.', null, 0, null, false, false, false, 10, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (999, 'Казачий Заряд', 'Cossack Charge', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, коньяк и шерри бренди. Процедить в бокал и подавтаь.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1000, 'Ледяная Вода Канзас Сити', 'Kansas City Ice Water', '', 2, 2, 4, 'В стакан со льдом налить водку, джин и перемешать. Налить сок лайма или отжать четвертинку свежего, дополнить Спрайтом или 7Up и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1001, 'Казино', 'Casino', '', 2, 2, 4, 'В шейкере со льдом встряхнуть джин, сок лимона, вишневый ликер и апельсиновый биттер. Процедить в бокал, украсить листочком мяты и подавать.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1002, 'Ледяной Кофе', 'Iced Coffee', '', 2, 2, 4, 'В стакан со льдом налить кофейный ликер, водку и дополнить свежесваренным охлажденным кофе.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1004, 'Итальянский Ледяной Чай', 'Italian Ice Tea', '', 2, 2, 4, 'В бокал положить несколько кубиков льда, налить красный вермут (на пример Martini Rosso vermouth) и дополнить имбирным элем. Украсить долькой апельсина и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1005, 'Кайн и Авель', 'Cane and Abel', '', 2, 4, 4, 'В высокую узкую стопку налить до краев хорошо олажденного рома, а в высокий стакан налить энергетический напиток до половины. (важно, чтобы рюмка помещалась внутрь стакана с запасом)
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1006, 'Ледяной Шот', 'Ice Cold Shot', '', 2, 4, 4, 'В охлажденную в морозильной камере рюмку налить сильно охлажденный бальзам Вана Таллин.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1010, 'Лексус', 'Lexus', '', 2, 4, 4, 'Врюмку налить сироп голубой Кюрасао, наслоить на него кофейный ликер, на него айриш крем, и, в последнюю очередь, самбуку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1013, 'Таллинский Какао', 'Tallin Cocoa', '', 2, 2, 4, 'В чашку налить какао, добавить Вана Таллтин, сверху выложить взбитые сливки. Перед подачей можно посыпать порошковым кофе, тертым шоколадом или корицей.', null, 0, null, false, true, false, 0, 'cup');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1014, 'Лемон Дроп Экстраординарный', 'Lemon Drop Extraordinaire', '', 2, 4, 4, 'Лимон Мейера разрезать на половинки и одной из них смочить края коктейльного бокала. Опустить бокал в сахар, чтобы сделать сахарную корочку по краям.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1015, 'Кактус', '', '', 2, 4, 4, 'В рюмку налить мятный лиекр, затем наслоить сок лимона и сверху наслоить текилу.', null, 0, null, false, true, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1016, 'Ленивая Ящерица', 'Lounge Lizard', '', 2, 2, 4, 'В бокал со льдом налить амаретто и темный ром. Перемешать и дополнить колой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1017, 'Кактусовый Джек', 'Cacktus Jack', '', 2, 4, 4, 'В шейкере со льдом встряхнуть текилу, голубой Кюрасао, апельсиновый, ананасовый и лимонный соки. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1018, 'Лесбийская Любовь', 'Lesbian Love', '', 2, 4, 4, 'В рюмку налить амаретто, наслоить на него мятный ликер (Creme de menthe), Амарула крем ликер и трипл сек.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1020, 'Эстонский Лесной Пожар', 'Estonian Forest-Fire', '', 2, 4, 4, 'Выпить залпом и закусить киви.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1021, 'Кофе Калипсо', 'Calypso Coffee', '', 2, 2, 4, 'В чашку для ирландского кофе налить апельсиновую водку, дополнить горячим кофе и украсить сверху взбитыми сливками.', null, 0, null, false, false, false, 60, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1022, 'Лесоруб', 'Rail Splitter', '', 2, 2, 4, 'В большой стакан налить сахарного сиропа, выжать сок лимона - по вкусу. Дополнить до краев стакан имбирным элем. Можно добавить несколько кубиков льда.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1023, 'Калифорнийский Винт', 'California Screw', '', 2, 2, 4, 'В стакан со льдом налить водку, грейпфрутовый и апельсиновый соки.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1024, 'Летающий Кенгуру', 'Flying Kangaroo', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, светлый ром, ликер Гальяно, апельсиновый сок, ананасовый сок, кокосовый крем и сливки. Перелить в большой бокал с колотым льдом. Украсить долькой ананаса и подавать.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1025, 'Калифорнийский Серфер', 'California Surfer', '', 2, 4, 4, 'В шейкере со льдом встряхнуть ром, ананасовый сок и Ягермастер, процедить в рюмки и подавать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1026, ' Летние Воспоминания', 'Summer Memoirs', '', 2, 2, 4, 'Встакан с несколькими кубиками льда положить нарезанную на кусочки клубнику. Добавить содку и дполнить Спрайтом или 7Up.', null, 0, null, false, false, false, 60, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1027, 'Калросс', 'Culross', '', 2, 4, 4, 'В шейкере со льдом встряхнуть светлый ром абрикосовый бренди, белое вино (желательно Lillet Blanc wine) и сок лимона. Процедить в бокал и подавтаь.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1028, 'Летний Дождь', 'Summer Rain', '', 2, 2, 4, 'В стакан, наполовину заполненный льдом, налить соки, апельсиновый джем, добавить малину и перемешать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1030, 'Летний Закат', 'Summer Sunset ', '', 2, 4, 4, 'Сешать равные части апельсинового и клюквенного сока, перелить в бокал, добавить немного Спрайта (при желании), украсить долькой лайма и поадвать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1031, 'Камикадзе', 'Kamikaze', '', 2, 4, 4, 'Рецепт от посетителя.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1033, 'Кампари Шейкерато', 'Campari Shakerato', '', 2, 4, 4, 'В шейкере со льдом встряхнуть Кампари и сок лимона. Процедить в бокал и подавать украсив спиралькой из корки лимона.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1034, 'Внезапный Ливень', 'April Shower', '', 2, 4, 4, 'В шейкере со льдом встряхнуть бренди, желтый Шартрёз и апельсиновый сок. ПРоцедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1035, 'Кампари Оранж', 'Campari Orange', '', 2, 2, 4, 'В стакане со льдом смешать Кампари и апельсиновый сок, украсить кружком из апельсина и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1036, ' Лизнуть Жабу', 'Lick a Toad', '', 2, 2, 4, 'Встакане смешать равные части водки и голубого Кюрасао. Долитьжелтым энергетическим напитком (Рэд Булл) по обретения зеленоватой жабьей окраски и подавать.', null, 0, null, false, false, false, 60, 'collins');
