INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1123, 'Лягушка в Блендере', 'Frog in a Blender', '', 2, 2, 4, 'В блендере со льдом хорошо пробить водку, апельсиновый сок и голубой Кюрасао. Перелить в бокал и по сленек влить в него немного гренадина.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1124, 'Питон', 'Butterbee', '', 2, 4, 4, 'В рюмку налить ирисовый шнапс и в него, стараясь не сильно перемешивать, влить такую же долю айриш крема', null, 0, null, false, true, false, 0, 'shot');
