INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1127, 'Грязный Мадагаскар', 'Muddy Madagascar', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать кофейный ликер и банановый нектар. Процедить в бокал, украсить долькой ананаса и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1128, 'Пламя Доктора Пеппера', 'Dr. Pepper Flame', '', 2, 4, 4, 'В рюмку налить две трети Амаретто и наслоить на него 151 ром. В стакан налить Доктор Пеппер. Поджечь рюмку и перед употреблением опустить ее в стакана Доктором Пеппером. Пить залпом.', null, 0, null, true, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1129, 'Мадонна', 'Madonna', '', 2, 4, 4, 'В рюмку налить вишневую водку, добавить коктейльную вишню и дополнить 7Up или Спрайтом.', null, 0, null, false, false, false, 0, 'sower');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1130, 'Плантаторский Пунш', 'Planter’s Punch', '', 2, 2, 4, 'В стакан со льдом налить темный ром, сахарный сироп, плеснуть ангостуры, отжать сок четвертинки лайма и дополнить содовой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1131, 'Мадрас', 'Madras', '', 2, 2, 4, 'Бокал Хайболл заполните кубиками льда примерно наполовину. Влейте водку, апельсиновый фреш (сок из пакета не подойдет), а затем клюквенный сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1132, 'Платиновая Пуля', 'Platinum Bullet', '', 2, 4, 4, 'В шейкере со льдом встряхнуть равные части водки и мятного ликера (Green peppermint schnapps). Процедить в замороженную рюмку и подавать.', null, 0, null, false, false, false, 0, 'shot');
