INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1137, 'Мак Дуг', 'Mac Dawg', '', 2, 2, 4, 'В стакан с несколькими кубиками льда налить пол баночки энергетического напитка, и водку, затем плеснуть немного сиропа гренадин.
