INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1200, 'Порто Флип', 'Porto Flip', '', 2, 4, 4, 'В шейкере со льдом встряхнуть коньяк, красный портвейн, яичный желток, сливки и сахарную пудру. Процедит в коктейльный бокал, посыпать тертым мускатным орехом и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1140, 'Греческая Подводная Лодка', 'Submarine Greece', '', 2, 4, 4, 'В старомодный стакан налить на две трети пива. В одну рюмку налить ванильную водку, а в другую - текилу. Установить обе рюмки на края стакана так, чтобы они соприкасались краями и не падали.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1141, 'Маленький Помощник Санты', 'Santa&#039;s Little Helper', '', 2, 2, 4, 'В шейкере со льдом встряхнуть яблочный сок, коричный шнапс, ирисовый шнапс и яблочный шнапс. Разлить в большие коктейльные бокалы и украсить яблоком или палочками корицы.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1142, 'Подружки Невесты', 'The Bridesmaids', '', 2, 2, 4, 'В бокал со льдом наливаем одну часть водки - невесту. И добавляем розового шампанского количество частей которого соответствует достаточному в вашем представлении количеству подружек невесты.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1143, 'Маленький Цветок', 'Petite Fleur', '', 2, 4, 4, 'В шейкере со льдом встряхнуть ром, трипл мек, ананасовый сок и процедть в бокал. Украсить ананасом и подавать.', null, 0, null, false, false, false, 0, 'martini');
