INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1148, 'Карибский Пожар Прерии', 'Caribbean Prairie Fire', '', 2, 4, 4, 'В рюмку налить крепкий ром, добавить Табаско и пить залпом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1150, 'Полет на Луну', 'Flight to the Moon', '', 2, 2, 4, 'В шейкере, на две трети заполненном льдом, встряхнуть водку, джин, текилу, ром, трипл сек и голубой Кюрасао. Процедить в стакан с несколькими кубиками льда и дополнить шампанским.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1153, 'Мальчики Тупые', 'Boys are Stupid', '', 2, 4, 4, 'Налить равные части крепкого рома, водки, айриш крема и кофейного ликера в стакан маргарита и подавать не перемешивая.', null, 0, null, false, false, false, 40, 'margarita');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1154, 'Полуночный Поцелуй', 'Midnight Kiss', '', 2, 2, 4, 'В бокал с сахарной корочкой налить водку, шампанское и немного голубого Кюрасао.', null, 0, null, false, false, false, 60, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1155, 'Мамин Маленький Помощник', 'Mama&#039;s Little Helper', '', 2, 2, 4, 'Стакан наполнить на три четверти льдом. Влить в него джин, сок лайма, сахарный сироп, огуречное пюре и перемешать. Дополнить стакан содовой и подавать украсив ломтиком огурца.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1188, 'Порочные Страсти Алены', 'Alena&#039;s Vicious Passions', '', 2, 4, 4, 'В шейкере со льдом встряхнуть Ягермастер и ананасовый сок, процедить в бокальчик и подавать.', null, 0, null, false, false, false, 0, 'sower');
