INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1164, 'Помошь в Самоубийстве', 'Assisted Suicide', '', 2, 4, 4, 'В стакане со льдом смешать ром и Егермейстер, перелить в рюмку и дополнить колой.', null, 0, null, false, false, false, 50, 'shot');
