INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1166, 'Помощник Куни', 'Kuni&#039;s Helper', '', 2, 2, 4, 'В блендере взбить клубничный ликер, молоко и мороженое. Разлить по бокалам, наполняя их на две трети и дополнить колой. Подавать с ложечкой.
