INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1170, 'Поп Бомб', 'Bomb Pop', '', 2, 4, 4, 'В высоком стакане смешать равные пропорции лимонада, рома и голубого кюрасао.', null, 0, null, false, false, false, 40, 'highball');
