INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1201, 'Между Ягодицами', 'Between The Cheeks', '', 2, 2, 4, 'Бокал наполнить льдом и влить в него трипл сек, сок лайма, бренди и темный ром. Добавить две коктейльные вишни и подавать с соломинкой.', null, 0, null, false, true, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1202, 'Порто Шоколад', 'Chocolate Porto', '', 2, 4, 4, 'В большую рюмку положить маленькую ложечку шоколадного мороженого (желательно, мороженого с темным шоколадом) и долить красный портвейн.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1203, 'Мексиканский Космонавт', 'Mexican Spaceman', '', 2, 4, 4, 'В немаленькую рюмку налить мятный ликер, наслоить на него сок лимона и сверху наслоить текилу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1204, 'Поручик Киже', 'lieutenant Kije', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, банановый ликер и апельсиновый сок. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1205, 'Мексиканский Серфборд', 'Mexican Surfboard', '', 2, 4, 4, 'В рюмку налить дынный ликер, на него наслоить голубой Кюрасао, и на него текилу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1206, 'После полуночи', 'After Midnight', '', 2, 2, 4, 'В стакан молока положить две ложки растворимого кофе, две ложки растворимого какао, размешать и добавить сливок для мягкости вкуса.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1209, 'Мемачау', 'Memachau', '', 2, 2, 4, 'В стакан налить пиво идобавить кофейный ликер.', null, 0, null, false, false, false, 60, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1210, 'После Секса', 'After Sex', '', 2, 2, 4, 'В Высокий стакан с несколькими кубиками льда налить водку, банановый ликер и апельсиновый сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1211, 'Мендельсон', 'Mendelssohn', '', 2, 2, 4, 'В высокий бокал положить лед и влить айриш крем, дополнить содовой и подавать. можно украсить коктейльной вишенкой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1212, 'Послед', 'Afterbirth', '', 2, 4, 4, 'В рюмку налить малиновый ликер, затем сироп гренадин. Последним наслоить Айриш крем и подождать с полминуты, пока он створожится  после чего, подавать.', null, 0, null, false, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1214, 'Последний Вздох', 'Last Sigh', '', 2, 4, 4, 'В шейкере со льдом встряхнуть абсент, самбуку, зеленый банановый ликер и кисло-сладкий микс. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1237, 'Миллионер', 'Millionaire', '', 2, 4, 4, 'В шейкекре со льдом встряхнуть бурбон, трипл сек, яичный белок и несколько капель гренадина. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1215, 'Мертвая Голова', 'Dead Head', '', 2, 4, 4, 'В рюмку налить водку, сок лимона, добавить Табаско, соус Ворчестер и черный молотый перец. Можно добавить еще каких-нибудь острых специй, на ваше усмотрение.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1216, 'Блондинистая Потаскуха', 'Blond Headed Slut', '', 2, 4, 4, 'Налить компоненты в стопку и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1218, 'Похмелье', 'Hangover', '', 2, 2, 4, 'В большой стакан налить водку (можно черносмородиновую), джин, виски и айриш крем. немного встряхнуть, но сильно не смешивать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1219, 'Мертвый Гитлер', 'Dead Hitler', '', 2, 4, 4, 'В рюмку налить равные части Ягерматсера, мятного ликера и коричного шнапса.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1220, 'Поцелуй Ангела', 'Angel&#039;s Kiss', '', 2, 4, 4, 'В шейкере со льдом смешать водку и ликер, затем процедить в коктейльный стакан и украсить его спиралькой из апельсиновой цедры.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1223, 'Кричащий Мертвый Нацист', 'Screaming Dead Nazi', '', 2, 4, 4, 'В рюмку налить мятный ликёр, наслоить ягермастер и, в завершение, крепкий ром. Поджечь и подавать. Перед употреблением - потушить.', null, 0, null, true, true, false, 60, 'shot');
