INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1240, 'Призрачная текила', 'Tequila Ghost', '', 2, 4, 4, 'Можно украсить засушенным цветком аниса.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1241, 'Кроваво-Апельсиновое Шампанское', 'Blood-Orange Champagne', '', 2, 2, 4, 'В бокал отжать половинку красного апельсина и дополнить его шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1242, 'Примавера', 'Primavera', '', 2, 4, 4, 'Украсить маленькой долькой апельсина и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1243, 'Шоколадно-Миндальное Восхищение', 'Choco Almond Delight', '', 2, 2, 4, 'В блендер налить охлажденный кофе, амаретто и шоколадное мороженое. Пробить все до вспенивания и перелить в бокал. Посыпать сверху тертым миндалем и подавать.', null, 0, null, false, false, false, 0, 'highball');
