INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1249, 'Мираж', 'Mirage', '', 2, 4, 4, 'В стопку наливается зелёный мятный ликёр, затем слоями, при помощи коктейльной ложечки, наливаются последовательно Айриш Крем ликёр, Трипл сек и Егермейстер. Коктейль переливается всеми цветами радуги, но крепость его такова, что, если употреблять его без меры, то можно самому начать видеть миражи.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1250, 'Простофиля', 'Simpleton', '', 2, 2, 4, 'В чашу для пунша налить клюквенный сок, гранатовый сок и отжать сок одного лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1252, 'Простой молочный', 'Simple Milk Cocktail', '', 2, 2, 4, 'Смешать ингредиенты в широком стакане, добавить соломинку и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1274, 'Бренди Молочный Пунш', 'Brandy Milk Punch', '', 2, 2, 4, 'В шейкере со льдом встряхнуть бренди, молоко и сахарную пудру. Процедить в бокал, присыпать тертым мускатным орехом и подавать.', null, 0, null, false, false, false, 50, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1255, 'Мичелада', 'Michelada', '', 2, 2, 4, 'В охлажденный пивной бокал с корочкой из соли (или из смеси соли и красного перца) положить несколько кубиков льда. Влить в него соус Барбекю (или Вустерширский соус, или смесь Вустерширского и соевого соуса, или сместь соуса Барбекю с томатным соусом или соком, или смесь соевого соуса и Табаско, или Томатный соус и Терияки и т.д.) примерно 30-50 мл, и отжать пол лайма. Подвать с бутылкой светлого пива.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1256, 'Противная слякоть', 'Nasty Slush', '', 2, 4, 4, 'В высокую стопку налить кофейный ликёр (калуа или любой другой), затем, при помощи коктейльной ложечки, наслоить Айриш крем, и в завершение наслоить Абсент.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1257, 'Зеленый Мишка Гамми', 'Green Gummy Bear', '', 2, 4, 4, 'В рюмку налить водку и дынный ликер, выдавить сок из дольки мандарина, плеснуть Спрайт или 7 Up и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1258, 'Противопехотный', 'Antiinfantry', '', 2, 4, 4, 'Налить половину рюмки спиртом и дополнить ее пивом.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1259, 'Млечный Путь', 'Milky Way', '', 2, 4, 4, 'Наслоить ингредиенты в рюмку так, чтобы в середине был Айриш крем.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1260, 'Противотуманные Фары', 'Fog Cutter', '', 2, 2, 4, 'В шейкере со льдом встряхнуть свежевыжатый апельсиновый сок, лимонный сок, чайную ложку миндального сиропа, светлый ром, джин и бренди. Перелить в бокал. Дополнить льдом и наслоить херес.
