INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1263, 'Плохие Мозги', 'Bad Brains', '', 2, 4, 4, 'В рюмке смешать равные части золотой текилы и Егермейстера.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1264, 'Прыгающие Зерна', 'Jumping Bean', '', 2, 4, 4, 'В стакане для смешивания со льдом соединить текилу и самбуку, процедить в коктейльный бокал и подавать. добавив несколько кофейных зерен.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1265, 'Мозговой штурм', 'Brainstorm', '', 2, 4, 4, 'В стакане для смешивания со льдом соединить компоненты,и перелить в коктейлный бокал.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1266, 'Босфорский Прыжок', 'Bosfor Jump', '', 2, 4, 4, 'В шейкере со льдом смешать абсент и лимонный сок, процедить в бокал, долить содовой и украсить спиралькой из лимонной цедры.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1267, 'Мой Злейший Враг', 'My Own Worst Enemy', '', 2, 2, 4, 'Опустить рюмку с ликерами в стакан с энергетиком и залпом выпить.', null, 0, null, false, false, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1268, 'Пузо', 'Mpouzo', '', 2, 4, 4, 'Врюмку стопку налить 10 мл Узо и дополнить светлым пивом.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1269, 'Третий Мировой Мокка', '3rd World Mocha', '', 2, 2, 4, 'В чашке смешать горячий кофе с сахором и молоком. Добавить темный крем де какао по вкусц и подавать.', null, 0, null, false, false, false, 0, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1270, 'Пуля в Голове', 'Bullet in the Head', '', 2, 4, 4, 'Налить в рюмку равные части Ягермастера, коричного шнапса, мятного ликера и самбуки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1271, 'Молодость', 'Youth', '', 2, 2, 4, 'Встакане для смешивания смешать 2-3 перепелиных яйца, добавить коньяк, колу, ложку сахара и сок дольки лимона. перемешать и процедить в стакан. долить содовой и подавать.', null, 0, null, false, false, false, 0, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1272, 'Пуля в голову', 'Bullet in the Head', '', 2, 2, 4, 'В стакан со льдом налить водку, джин, текилу, ром, сок лимона и сахарный сироп. Дополнить до краев колой и перелить в блендер, взбить до консистенции смузи и перелить назад в бокал.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1273, 'Молоко Гориллы', 'Gorilla Milk', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, коричневый крем де какао и молоко. Процедить в бокал с несколькими кубиками льда и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1278, 'Пурпурный Гудок', 'Purple Hooter', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, Малиновый ликер (Chambord) и Спрайт или 7 Up. Процедить в стопку и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1280, 'Пус Кафе', 'Pousse Cafe', '', 2, 4, 4, 'Pousse Cafe - устойчивое словосочетание во французском языке, обозначающее рюмочку бренди, ликера или коктейля после кофе. Это очень старая традиция и Пус кафе включает в себя массу слоистых коктейлей в рюмочке, которая имеет такое же название (рюмочка на ножке с расширением к краям.)
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1281, 'Огненный Молоток', 'Firehammer', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, амаретто, трипл сек и сок дольки лимона. Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1282, 'Пушистый Пес', 'Fluffy Dog', '', 2, 2, 4, 'Также, можно встряхнуть их в шейкере со льдом и процедить в бокал.', null, 0, null, false, false, false, 60, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1283, 'Молочай', 'Poinsettia', '', 2, 2, 4, 'В бокал для шампанского налить трипл сек, клюквенный сок и дополнить шампанским. украсить колечком из цедры апельсина и подавать.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1284, 'Пушистый Утенок', 'Fluffy Duck', '', 2, 2, 4, 'Смешать яичный ликер (например, Адвокат), трипл сек (например, Cointreau) и сливки. Долить лимонад и подавать с соломинкой.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1347, 'На Броневик!', 'On an armored car!', '', 2, 4, 4, 'В бокал налить равные части водки, шампанского (желательно Броют) и темного пива.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1285, 'Молочный коктейль', 'Milkshake', '', 2, 2, 4, 'Самый простой молочный коктейль в домашних условиях можно приготовить при помощи блендера и имея молоко и мороженое.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1286, 'Пушкин Тини', 'Pushkin Tini', '', 2, 4, 4, 'В шейкер положить 20 г клубники, 20 г ревеня и размять мадлером. Добавить водку, сок лимона и сахарный сироп. Засыпать лед, встряхнуть и процедить в бокал.', null, 0, null, false, false, false, 20, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1287, 'Молпси', 'Milpsi', '', 2, 2, 4, 'В высоком стакане смешать 6 частей колы и 1 часть молока.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1288, 'Пушкинский Молочный', 'Pushkin&#039;s Milkshake', '', 2, 2, 4, 'Смешать кофейную водку, апельсиновую водку, молоко и какао. Налить в винный бокал, добавить сверху взбитые сливки, посыпать тертым мускатным орехом и подавать.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1289, 'Монако', 'Monaco', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1290, 'Пчела', 'Bee', '', 2, 2, 4, 'В высокий бокал со льдом налить водку, банановый ликер, имбирный эль, перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
