INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1292, 'Пылающая Дыня', 'Flaming Melon', '', 2, 4, 4, 'В рюмку налить дынный ликер и наслоить на него спирт. Поджечь и подавать. Не нужно позволять пылать коктейлю больше 10 секунд, потому что нагреются края и могут обжечь губы.', null, 0, null, true, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1877, 'Топливный впрыск', 'Fuel Injection', '', 2, 4, 4, 'В рюмку налить половину темного рома и половину абсента.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1294, 'Пылаюшая Самбука', 'Flaming Sambuca', '', 2, 4, 4, 'В высокую рюмку налить Самбуку и добавить три зернышка кофе. Поджечь, дать погореть около 10 секунд, накрыть ладонью, чтобы погасить пламя.Вдохнуть пары из под ладони, выпить содержимое рюмки и зажевать зернами кофе.', null, 0, null, true, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1295, 'Мордовский особый', 'Special Mordovian', '', 2, 2, 4, 'В пивной бокал налить водку и жидкий мёд, и смешать с мелко колотым льдом до полного растворения. После чего влить светлое пиво.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1296, 'Пылающий Голубой Фак', 'Flaming Blue Fuck', '', 2, 4, 4, 'Врюмку налить равные части голубого Кюрасао и самбуки. Поджечь и быстро выпить через соломинку.', null, 0, null, true, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1298, 'Пылающий Голубой Иисус', 'Flaming Blue Jesus', '', 2, 4, 4, 'В рюмке смешать треть текилы, треть мятного шнапса, треть персикового ликера и наслоить немного крепкого рома, для того, чтобы Иисус пылал.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1348, 'Райское Наслаждение', 'Paradise Pleasure', '', 2, 2, 4, 'Молоко (лучше пакет) положить на полчаса в морозильник, до образования кристалликов льда.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1299, 'Клюквенный Морс', 'Cranberry Mors', '', 2, 2, 4, 'Когда масса становится однородной и в ней уже не различимы косточки и клочки кожицы, вливаем остатки воды, еще немного перемешиваем и помещаем морс в холодильник, чтобы он немного остыл и настоялся. Через полчаса морс можно разливать по бокалам и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1301, 'Дно Морское', 'Sea Bottom', '', 2, 2, 4, 'Стакан наполнить льдом и влить в него водку, айриш крем, голубой Кюрасао и дополнить колой. Украсить кружком лайма или коктейльной вишней и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1302, 'Пылающий Дракон', 'Flaming Dragon', '', 2, 4, 4, 'Смешать крепкий ром, зеленый Шартрёз и поджечь.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1303, 'Морской бриз', 'Sea Breeze', '', 2, 2, 4, 'В стакан охлажденный в морозильнике налить додку, свежевыжатый грейпфрутовый сок и клюквенный сок. украсить спиралькой из едры лимона и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1306, 'Пылающий Жираф', 'Flaming Giraffe', '', 2, 4, 4, 'Поджечь, и подавать.', null, 0, null, true, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1307, 'Московский Новый Год', 'Moscow New Year', '', 2, 4, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1308, 'Пылающий Засранец', 'Flaming Asshole', '', 2, 4, 4, 'В рюмку налить слоями сироп гренадин, мятный ликер, банановый ликер, крепкий ром, поджечь и подавать с соломинкой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1309, 'Моторное Масло', 'Motor Oil', '', 2, 4, 4, 'Вбокал налить Ягермастер, за тем по порядку - мятный шнапс, коричный шнапс и кокосовый ром.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1311, 'Мотылек', 'Moth', '', 2, 4, 4, 'Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1312, 'Пылающий Мексиканский Флаг', 'Flaming Mexican Flag', '', 2, 4, 4, 'В большую стопку налить гренадин, на него наслоить крем де менте, сверху текилу и на самый верх крепкий ром. Поджечь и подавать.', null, 0, null, true, true, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1941, 'Убить Билла', 'Kill Bill', '', 2, 4, 4, 'В рюмку уложить слоями лимончелло и черную самбуку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1313, 'Текила Мохито', 'Tequila Mojitos', '', 2, 2, 4, 'Сахарный сироп подогреть до 50-60 градусов, добавить в него несколько листьев мяты и хорошенько размять. Дать отстояться пару часов, затем остудить в холодильнике.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1314, 'Пылающий Моз', 'Flaming Moz', '', 2, 4, 4, 'Через 10 секунд тонкой струйкой перелейте горящую жидкость в бокал. Перелив все, накройте перевернутым коньячным, бокал с пылающей смесью и дождитель, пока все угаснет.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1315, 'Оахакский Мул', 'Oaxaca Mule', '', 2, 2, 4, 'В кружку или в высокий стакан положить лёд, влить текилу (Мезкаль - mezcal подойдет лучше всего) отжать две четвертинки лайма и дополнить имбирным элем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1316, 'Пылающий Раста', 'Flaming Rasta', '', 2, 4, 4, 'Облизать соломинку, поджечь коктейль и быстро выпить его через соломинку.', null, 0, null, true, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1413, 'Ниндзя', 'Ninja', '', 2, 2, 4, 'В шейкер со льдом влить 151 ром, кокосвый ром, плеснуть гренадина и ананасового сока. Встряхнуть, процедить в бокал и подавать.', null, 0, null, false, false, false, 10, 'collins');
