INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1319, 'Ирландская Мусорная Корзина', 'Irish Trash Can', '', 2, 2, 4, 'Высокий бокал на две трети наполнить льдом и влить в него водку, джин, светлый ром, трипл сек, персиковый шнапс и голубой Кюрасао в равных долях. Хорошенько перемешать и перевернуть в него открытую баночку энергетического напитка. Вставить сгибающуюся соломинку и подавть.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1321, 'Муссолини', 'Mussolini', '', 2, 4, 4, 'В рюмку налить равные части коричного шнапса, самбуки и Ягермастера.', null, 0, null, false, false, false, 0, 'shot');
