INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1351, 'Набор Отверток', 'Screw-Drivers Set', '', 2, 2, 4, 'В нескольких стаканах со льдом смешать водку с разными соками (подойдут любые из ближайшего магазина) украсить кружками из апельсина добавить соломинки и подавать.
