INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1360, 'Раффаэлло', 'Raffaello', '', 2, 2, 4, 'В шейкере со льдом хорошенько встряхнуть ванильную водку, кокосовый ром ликер, кокосовое молоко, миндальный сироп и сливки. Процедить в бокал с несколькими кубиками льда и подавать с конфеткой Раффаэлло.
