INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1362, 'Рахат Лукум', 'Rahat Lukum', '', 2, 2, 4, 'Края бокала смочить и обмакнуть в тертый шоколад. В шейкере со льдом встряхнуть голубой Кюрасао, кофейный ликер, Гальяно и молоко. В бокал влить гренадин и распределить его по стеночкам, затем процедить содержимое шейкера и сверху добавить взбитые сливки.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1363, 'Намек Ангела', 'Angel&#039;s Tip', '', 2, 4, 4, 'В рюмку налить темный крем де какао и наслоить на негь сливки, можно взбитые. Украсить вишенкой насаженной на зубочистку и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1382, 'Рейнджер', 'Ragnar', '', 2, 2, 4, 'В высокий бокал налить черносмородиновую водку (Absolut Kurant) добавить несколько капель сока лайма и дополнить 7Up.
