INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1391, 'Негр в Пене', 'The Negro in Foam', '', 2, 2, 4, 'В стакан налить водку, добавить ложку растворимого кофе и дополнить колой.  Будьте внимательны и осторожны - негр сильно пенится!
