INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1445, 'Обезьяний Мозг', 'Monkey Brain', '', 2, 4, 4, 'В рюмку налить яблочный ликер (Apfelkorn),почти до краев. капнуть несколько капель айриш крема так, чтобы ликеры не смешивались и капли оставались целыми в толще яблочного ликера.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1447, 'Возбужденная Обезьянка', 'Horny Monkey', '', 2, 4, 4, 'В рюмку налить черную самбуку и наслоить на нее банановый ликер.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1448, 'Рубиновый Вторник', 'Ruby Tuesday', '', 2, 2, 4, 'В бокал со льдом налить джин, гренадин и дополнить клюквенным соком.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1449, 'Обезьянка-Астронавт ', 'Space Monkey', '', 2, 4, 4, 'В стакане для смешивания или кувшине смешать водку, текилу и грейпфрутовый сок. Разлить по рюмкам, наливая чуть меньше трех четвертей в каждую. Долить во все рюмки спрайт, так чтобы рюмка была полная, но содержимое не касалось сахарных краев.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1450, 'Русская Конфетка', 'Russian Candy', '', 2, 4, 4, 'В рюмку налить водку и персиковый шнапс, после чего аккуратно влить ложечку сиропа, не перемешивать, дать гренадину осесть на дно и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1451, 'Обер-Егермейстер', 'Master of The Hounds', '', 2, 4, 4, 'Процедить в бокал и подавть.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1452, 'Русская Красавица', 'The Russian Beauty', '', 2, 4, 4, 'В шейкере встряхнуть водку, розовый вермут и гранатовый сок. Перелить в коктейльный бокал и украсив долькой лимона, подавать.', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1453, 'Облако', 'Cloud', '', 2, 4, 4, 'В рюмку налить самбуку, на нее наслоить текилу и на нее - абсент. Добавить несколько капель айриш крема и голубого Кюрасао.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1454, 'Русская рулетка', 'Russian Roulette', '', 2, 4, 4, 'Налить в рюмки водку и кофейный ликёр, накрыть сверху кружочком апельсина.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1455, 'Обморожение', 'Frost Bite', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку и белое вино из замороженного винограда (Icewine - особый сорт вина, которое готовят из винограда замерзшего на ветках) процедить в бокал, добавить несколько виноградин и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1456, 'Руская Тройка', 'Russian Three', '', 2, 2, 4, 'В стакан со льдом налить водку, лимонный сок и дополнить имбирным элем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1457, 'Обойма', 'Cartridge Clip', '', 2, 4, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1458, 'Русский Бежевый', 'Beige Russian', '', 2, 2, 4, 'В бокал со льдом налить водку, кофейный ликер и молоко.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1460, 'Русский Бранч', 'Russian Brunch', '', 2, 2, 4, 'В блендере со льдом смешать водку и апельсиновый сок и перелить в высокий стакан. Дополнить стакан шампанским и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1461, 'Обратная тяга', '', '', 2, 4, 4, 'Налейте в винный бокал оба напитка и подожгите. Через несколько секунд накройте ладонью и дождитесь угасания. Вставьте в бокал под руку соломинку и, не выпуская паров выпейте напиток.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1464, 'Русский Зауэр', 'Russian Sauer', '', 2, 4, 4, 'Виски Jameson &ndash; 50 мл,
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1465, 'Огнемет', 'Flame Thrower', '', 2, 4, 4, 'В рюмку налить граппу и персиковый ликер. В чайную ложку налить крепкий ром и поджечь. Из ложки горящий ром струйкой налить в рюмку. Погасить и залпом выпить.', null, 0, null, true, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1467, 'Огни Гаваны', 'Lights of Havana', '', 2, 4, 4, 'Не нужно трясти слишком долго или сильно, потому что содовая выветрится.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1468, 'Русский Восход Солнца', 'Russian Sunrise', '', 2, 2, 4, 'Украсить по вкусу и подавать с трубочкой.', null, 0, null, false, false, false, 40, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1469, 'Огонь Души', 'Soulfire', '', 2, 4, 4, 'В рюмку налить коричный шнапс, сок из овощей и добавит ьпо паре капель Табаско и сока лимона, перемешать и подавать.', null, 0, null, false, false, false, 10, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1537, 'Падение Звезды в Алабаме', 'Stars Fell on Alabama', '', 2, 2, 4, 'В шейкере со льдом смешать все компоненты, перелить в бокал', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1470, 'Русско-Японская Война', 'Russian-Japoneese War', '', 2, 4, 4, 'В рюмку положить коктейльную вишненку и налить дынный ликёр, затем наслоить водку и капнуть лимонного сока.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1471, 'Оживляющий из Метвых', 'Corpse Reviver', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать две части коньяка, одну часть кальвадос и одну часть красного сладкого вермута. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1472, 'Рыжая Миранда', 'Red Headed Miranda', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, гранатовый сок и сок лимона. Процедить в бокал и подавать, украсив веточуой мяты.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1473, 'Оксана', 'Oksana', '', 2, 2, 4, 'Края стакана смочить водой или лимонным соком и обмакнуть в сахар и сделать корочку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1478, 'С Новым Годом', 'Happy New Year', '', 2, 2, 4, 'В шейкере со льдом встряхнуть бренди, красный портвейн, апельсиновый сок, перелить в бокал и дополнить шампанским.', null, 0, null, false, false, false, 50, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1479, 'Оле', 'Ole', '', 2, 4, 4, 'Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1482, 'Садовник', 'Gardener', '', 2, 2, 4, 'В блендере пробить до хорошего вспенивания сок моркоми и томатный сок (можно использовать очищенный от кржуры помидор) перелить в бокал и украсить петрушкой. Сольдобавлять по вкусу.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1525, 'Отражение', 'Backfire', '', 2, 4, 4, 'В стакан налить кофейный ликёр (Калуа или любой другой), затем водку и Айриш крем.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1485, 'Онотоле Кофе', 'Anatole Coffee', '', 2, 2, 4, 'В блендере взбить колотый лёд, охлажденный кофе, коньяк, кофейный и арахисовый ликеры, перелить в винный бокал и, украсив взбитыми сливками, подавать с соломинкой.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1486, 'Риц Сайдкар', 'Ritz Sidecar', '', 2, 4, 4, 'Края коктейльного бокала смочить лимонным соком и опустить в сахар для образования сладкой каёмки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1487, 'Огненный Ополаскиватель', 'Flaming Mouth Wash', '', 2, 4, 4, 'В рюмку налить равные части голубого Кюрасао и зеленого крем де менте. Влить столовую ложку спирта, перемешать и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1488, 'Саке Бомб', 'Sake Bomb', '', 2, 4, 4, 'Налить в стакан пиво, а в рюмку саке, положить на стакан две палочки для еды и поставить на них рюмку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1489, 'Опухоль Мозга', 'Brain Tumor', '', 2, 4, 4, 'В рюмку налить вермут, в него айриш крем. Капнуть 2-3 капли гренадина аккуратно и долить водки.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1491, 'Орангутан', 'Orangutan', '', 2, 2, 4, 'В бокале со льдом смешать джин, Фанту и красный грейпфрутовый сок.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1493, 'Оранжевый Бум', 'Orange Boom', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1495, 'Оранж Кола', 'Orange Cola', '', 2, 2, 4, 'В стакан со льдом налить равные части водки, колы и апельсинового сока.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1497, 'Оранжевый апельсин', 'Orange Orange', '', 2, 4, 4, 'На зубочистку насадить три ягодки клюквы и полоску апельсиновой цедры, украсить бокал и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1498, 'Сальная Душа', 'Greasy Soul', '', 2, 4, 4, 'В рюмку налить виски и положить полложки майонеза. Выпить заслпом, пока майонез не растворился и не растекся.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1519, 'Отвёртка Филлипс', 'Phillips screwdriver', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать трипл сек, водку и апельсиновый сок. Перелить в бокал Хайболл, и долить содовой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1517, 'Отважный Капитан', 'Captain Courageous', '', 2, 2, 4, 'По вкусу, или для украшения, можно чуть присыпать молотой корицей.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1568, 'Сердцеед', 'Heartbreaker', '', 2, 2, 4, 'В бокал налить ром и дополнить розовым игристым вином.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1500, 'Самбука с Мухой', 'Sambuca Con La Mosca', '', 2, 4, 4, 'В бокал для коньяка поместить 3-5 зерен кофе, налить самбкуку и поджечь. дать погореть около 10 секннд и перелить горящую жидкость с зернами в старомодный бокал и накрыть коньячным.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1501, 'Оргазм Януша', 'Janush Orgasm', '', 2, 2, 4, 'В блендере взбить мороженое и холодный вареный кофе, Перелить в бокал и дополнить колой.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1502, 'Самоубийственный', 'Suicidal', '', 2, 4, 4, 'В рюмку налить равные части водки, спирта и 151 рома.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1503, 'Орешек', '', '', 2, 4, 4, 'Налейте в стопку с кубиком льда водку и орехровый ликер.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1505, 'Орешки и Ягодки', 'Nuts &amp; Berries', '', 2, 4, 4, 'В коктейльную рюмку налить арахисовый ликер (Frangelico), и наслоить на него малиновый ликер.', null, 0, null, false, true, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1507, 'Чёрная Орхидея', 'Black Orchid', '', 2, 4, 4, 'Смешать темный ром, голубой Кюрасао, гренадин и перелить в рюмку.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1508, 'Сан Франциско', 'San Francisco', '', 2, 2, 4, 'В стакан положить несколько кубиков льда, налить водку, банановый ликер, апельсиновый сок и перемешать. Затем аккуратно, по стеночке налить гренадин, украсить долькой апельсина и подавать.', null, 0, null, false, true, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1557, 'Пароход', 'Пароход', '', 2, 2, 4, 'В бокал со льдом налить равные части водки, кофейного ликера и взбитых сливок. Перемешать и подавть.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1510, 'Сан Хуан Кофе', 'Cafe San Juan', '', 2, 2, 4, 'Сварить кофе, добавить в него сахар и дать енапитку остыть. В бокал для красного вина положить несколько кубиков льда, добавить ром и влить охлажденный сладкий кофе. Украсить долькой лимона и подавать.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1511, 'Остров Молока', 'Island Milk', '', 2, 2, 4, 'В блендер поместить чашку колотого льда, две ложки сахара, чашку холодного молока и добавить чашку апельсинового сока.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1513, 'Быстрые Острые Ощущщения', 'Quick Thrill', '', 2, 2, 4, 'В винный бокал с колотым льдом налить темный ром, красное вино и дополнить колой.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1514, 'Сангрия', 'Sangria', '', 2, 2, 4, 'В кувшин поместить несколько бутончиков сушеной гвоздики, палочку корицы, добавить сахар и нарезанные на кубики апельсины, персики и яблоки. Дополнить льдом, сухим красным вином, содовой сухим вермутом и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1515, 'От Заката до Рассвета', 'From Dusk Till Dawn', '', 2, 2, 4, 'В стакан со льдом налить красный вермут, водку, тоник и сок лимона.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1516, 'Санта Моника', 'Santa Monica', '', 2, 2, 4, 'В стакан со льдом налить кокосовый ром и дополнить энергетическим напитком.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1521, 'Отличный Виноград', 'Great Grapes', '', 2, 2, 4, 'В блендере до полного смешивания смешать одну часть виноградного сока, одну часть содовой, ложку мёда и ложку сока лимона. Разлить по бокалам и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1526, 'Свежесть', 'Freshness', '', 2, 2, 4, 'В стакане смешиваем мёд с яблочным соком и небольшим количеством ванилина (на кончике ножа), дополняем квасом и подаем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1527, 'Отсутствующий друг', 'Absent Friend', '', 2, 4, 4, 'В шейкере смешать джин, сироп гренадин, яичный белок и взбитые сливки, перелить в винный бокал, чуть посыпать тертым мускатным орехом и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1528, 'Свежий Воздух', 'Fresh Air', '', 2, 2, 4, 'В шейкере со льдом встряхнуть джин, сухой белый вермут и мятный ликер. Перелить в бокал и, украсив долькой лимона, подавать.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1529, 'Охота на всю ночь.', 'All Night Hunter', '', 2, 4, 4, 'Смешать в стопке компоненты и подавать.', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1532, 'Свенгали', 'Svengoolie', '', 2, 2, 4, 'В бокал со льдом налить текилу и виски, дополнить колой и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1533, 'ОЦХ', 'UCH', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1534, 'Светлый маг Дамблдор', 'Albus Dumbledore', '', 2, 4, 4, 'Край коктейльного стакана смочить лимонным соком и обмакнуть в сахарную пудру. Налить в стакан водку и плеснуть немного лимонного сока.', null, 0, null, false, false, false, 20, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1535, 'Падающая Звезда', 'Falling Star', '', 2, 4, 4, 'В рюмку налить самбуку на три четверти, Аккуратно наслоиь на нее бурбон (Jack Daniel''s Tennessee whiskey) и незамедлительно подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1538, 'Светлячок', 'Firefly', '', 2, 2, 4, 'В стакан со льдом налить водку, грейпфрутовый сок и в середину влить чайную ложку гренадина, постаравшись, чтобы он не перемешался, а так и остался в середине стакана.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1539, 'Падший Ангел', 'Fallen Angel', '', 2, 4, 4, 'В шейкере со льдом смешать все компоненты. Перелить в бокал и добавить вишенку.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1540, 'Перевернутый Светофор', 'Inverted Traffic Light', '', 2, 2, 4, 'Наполнить коктейльный бокал мелко колотым льдом и влить в него апельсиновый сок с персиковым шнапсом, до 3/4. Перемешать, чтобы все окрасилось в желтый. По краешку налить гренадин так, чтобы он осел на дно (можно помогать, приподнимая лёд ложечкой).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1541, 'Пакман', 'Pac Man', '', 2, 2, 4, 'В стакан со льдом налить гренадин, сок лимона и плеснуть апельсиновый биттер. Дополнить бокал имбирным элем и подавать, украсив кружком из апельсина.', null, 0, null, false, false, false, 10, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1542, 'Свидетель Мафии', 'Mob Witnesses', '', 2, 2, 4, 'В стакан со льдом налить одну часть амаретто и четыре части Доктор Перрер колы.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1543, 'Палома', 'Paloma', '', 2, 2, 4, 'Напольнить стакан льдом, влить в него текилу и содовую. Сверху отжать дольку лайма и подавтаь.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1544, 'Валенсийский Свизл', 'Valencia Swizzle', '', 2, 2, 4, 'В стакан налить апельсиновую водку, сок лимона и крем де какао, дополнить льдом перемешать коктейльной палочкой, украсить долькой апельсина и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1545, 'Папаша Алёша', 'Daddy Alesha', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, какао и сливки. Процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1546, 'Ванильная Свинья', 'Vanilla Pig', '', 2, 2, 4, 'Если в бокал со льдом налить 60 мл ванильной водки и 180 мл Доктора Пеппера, то получится Ванильная Свинья', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1547, 'Паралич', 'Paralysis', '', 2, 4, 4, 'В рюмку налить персиковый шнапс, наслоить на него ягермастер и сверху наслоить абсент.', null, 0, null, false, true, false, 30, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1548, 'Святой Валентин', 'Saint Valentine', '', 2, 4, 4, 'В шейкере со льдом встряхнуть ром, красный портвейн, ликер Гранд Марнье, свежевыжатый апельсиновый сок. Процедить в коктейльный бокал и подавать, украсив спиралькой из корки апельсина.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1549, 'Паранойя', 'Paranoia', '', 2, 4, 4, 'В рюмку налить амаретто и добавить апельсиновый сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1550, 'Святой Климент', 'Saint Clement', '', 2, 2, 4, 'Добавить дольку апельсина и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1551, 'Парашют', 'Parachute', '', 2, 2, 4, 'В шейкер, заполненный до половины колотым льдом, влить Коньяк, Кирш, Белок от одного яйца, всыпать ложку Растворимого кофе. Перемешать и влить в бокал с несколькими кубиками льда, затем, долить до верху Содовой.', null, 0, null, false, false, false, 20, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1552, 'Северное сияние', 'Aurora Borealis', '', 2, 4, 4, 'В бокал для шампанского налить спирта, затем, долить до краёв шампанского.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1553, 'Париж', 'Paris', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, сухой вермут и черносмородиновый ликер (крем де Кассис). Процедить в бокал, украсить спиральекой из корки лимона и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1554, 'Безопасный Секс на Пляже', 'Safe Sex on the Beach', '', 2, 2, 4, 'В стакан со льдом налить клюквенный сок, персиковый нектар и грейпфрутовый сок, украсить коктейльной вишней и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1560, 'Сентябрьское Утро', 'September Morn', '', 2, 4, 4, 'В шейере со льдом встряхнуть светлый ром, яичный белок, сок лайма, гренадин и сахарную пудру, процедить в бокал и подавть.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1561, 'Патио Лимонад', 'Patio Lemonade', '', 2, 2, 4, 'В шейкере, на две трети заполненном колотым льдом,встряхните компоненты и перелейте в бокал, добавьте несколько долек лимона.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1563, 'Патриот', 'Patriot', '', 2, 4, 4, 'В рюмку налить одну треть гренадина, на него наслоить треть голубого Кюрасао и сверху наслоить водку.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1564, 'Сердечный приступ', 'Heart Attack', '', 2, 2, 4, 'В высокий стакан со льдом налить водки и энергетического напитка.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1565, 'Патриотини', 'Patriotini', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку и спрайт. Перелить в коктейльный бокал. Аккуратно, по стеночке под водку налить гренадин, дать ему устояться и осесть. По ложечке, крайне аккуратно под водку, на гренадин наслоить голубой Кюрасао. Дать коктейлю еще немного осесть и успокоиться, после чего - подавать.', null, 0, null, false, true, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1566, 'Сердце', 'Corazon', '', 2, 4, 4, 'Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1567, 'Пацифико', 'Pacifico', '', 2, 4, 4, 'Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1569, 'Паяц', 'Clown', '', 2, 2, 4, 'В сткан влить гальяно, водку и сок лимона. Перемешать и добавить несколько кубиков льда. Дополнить Спрайтом или 7Up и подавать, украсив долькой лимона.', null, 0, null, false, true, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1570, 'Серебряная Пуля', 'Silver Bullet', '', 2, 4, 4, 'В рюмку налить кофейный ликер, положить маленькую дольку лимона и наслоить текилу.', null, 0, null, false, true, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1571, 'Пеликан', 'Pelican', '', 2, 2, 4, 'В стакане со льдом смешать грейпфрутовый сок, сок лайма (или лаймовый сироп), сок лимона и гренадин.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1574, 'Серп и Молот', 'Hammer &amp; Sickle', '', 2, 2, 4, 'В бокал для шампанского налить четверть бальзама Вана Таллин и три четверти шампанского.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1576, 'Серф Райдер', 'Surf Rider', '', 2, 2, 4, 'В бокале со льдом смешать светлый ром, клюквенный, и яблочный соки. Украсить долькой лимона и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1577, 'Пенящийся Мартини', 'Martini Fizz', '', 2, 4, 4, 'Для приготовления Martini Fizz смешать 75 мл Martini Rosato и 75 мл Martini Asti в бокале для красного вина, добавить несколько кубиков льда и украсить ягодами свежей малины.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1578, 'Серфер под Кислотой', 'Surfer On Acid', '', 2, 4, 4, 'В шейкере со льдом встряхнуть кокосовый ром, Ягермастер и ананасовый сок. Процедить врюмки и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1579, 'Первоклашка', 'First-Grader', '', 2, 2, 4, 'В посуде смешать морковный и яблочный соки, добавить сахар и долить молоко. Разлить по стаканам и подавать немедленно.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1636, 'Френч Кан Кан', 'French Can Can', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать абсент, ржаной виски и сухой вермут, процедить в коктейльный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1580, 'Сессия', 'Session', '', 2, 2, 4, 'В стакан положить 2 ложки растворимого кофе, талетку растворимого аспирина и 0.5 грамма витамин "С". Залить энергетическим напитком и аккуратно размешать до полного растворения порошка.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1633, 'Синий Мексиканец', 'Blue Mexican', '', 2, 4, 4, 'В рюмку налить слоями треть голубого Кюрасао, треть сока лимона, треть текилы и подавать.', null, 0, null, false, true, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1634, 'Французский 76', 'French 76', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, гренадин, сок лимона и сахар, процедитьв бокал и дополнить шампанским.', null, 0, null, false, false, false, 40, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1611, 'Seadoo', '', '', 2, 2, 4, 'Перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1612, 'Фонтан молодости', 'Fountain of Youth', '', 2, 2, 4, 'В соковыжималке отжать один очищенный от кожуры огурец, один очищенный красный или жёлтый сладкий перец, один стебель сельдерея и половину яблока. Перелить в бокал со льдом и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1613, 'Сибирский Мороз', 'Siberian Frost', '', 2, 4, 4, 'Напитки должны быть охлаждены в морозильной камере.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1614, 'Французская Революция', 'French Revolution', '', 2, 2, 4, 'В стакан для смешивания со льдом налить бренди и малиновый ликер, перемешать. Перелить в бокал для вина и дополнить шампанским.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1615, 'Кубинская Сигара', 'Cuban Cigar', '', 2, 4, 4, 'Вшейкере со льдом встряхнуть айриш крем и темный крем де какао. Процедить в коктейльный бокал и подавтаь.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1618, 'Французский 75', 'French 75', '', 2, 2, 4, 'В шейкере со льдом встряхнуть коньяк, сахар и сок лимона. ПРоцедить в бокалы, дополнить шампанским, украсить цедрой лимона и подавать.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1619, 'Сила Ананаса', 'Pineapple Power', '', 2, 2, 4, 'Можно использовать и свежий ананас, грамм 200-300.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1621, 'Сила Молока', 'The Power Of Milk', '', 2, 2, 4, 'В стакане со льдом размешать сахар и водку, добавить молоко и подавать с соломинкой.', null, 0, null, false, false, false, 20, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1622, 'Французский Поцелуй', 'French Kiss', '', 2, 4, 4, 'В шейкере встряхнуть водку, крем де какао темный, малиновый ликер и сливки.  Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1627, 'Сингапурская Петля', 'Singapore Sling', '', 2, 2, 4, 'В стакан налить гренадин и наполнить стакан льдом, долить джин, содовую и кисло-сладкий микс. Сверху налить шери бренди, украсить коктейльной вишней и подавать не перемешивая.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1628, 'Самбука Кофе Фраппе', 'Sambuca Coffee Frappe', '', 2, 4, 4, 'В шейкере с небольшим количеством сильно колотого льда встряхнуть кофейный ликер и выложить в коктейльный бокал, за тем с тем же количеством колотого льда встряхнуть самбуку и выложить поверх кофейного.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1629, 'Синий Дьявол', 'Blue Devil', '', 2, 4, 4, 'В шейкере со льдом встряхнуть джин, голубой Кюрасао, сок половины лимона и пол ложечки вишневого ликера (maraschino liqueur). Процедить в бокал и подавать.', null, 0, null, false, false, false, 40, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1630, 'Фредди Крюгер', 'Freddy Kruger', '', 2, 4, 4, 'Врюмку налить равные части водки, Ягермастера и самбуки.
