INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1654, 'Хан Соло', 'Han Solo', '', 2, 2, 4, 'В стакан со льдом налить водку, джин, светлый ром, бурбон, трипл сек кисло-сладкий микс и сверху налить 30 мл виски Wild Turkey 101', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1637, 'Сицилийский Всплеск', 'Sicilian Splash', '', 2, 2, 4, 'В бокал с 4 кубиками льда влить сок из белого винограда и дополнить содовой. Украсить бокал лимоном и подавать с трубочкой.', null, 0, null, false, false, false, 0, 'hurricane');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1638, 'Коста Риканский Дынный Фреско', 'Costa Rican Melon Fresco', '', 2, 2, 4, 'В блендер поместить очищеную от корки и семечек дыню, добавить сахар воду и сок лайма. Хорошенько пробить и разлиь по бокалам.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1639, 'Скарлетт О&#039;Хара', 'Scarlett O&#039;Hara', '', 2, 4, 4, 'В шейкере со льдом встряхнуть Саузен Комфорт, клюквенный сок и сок лимона. Процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1640, 'Фростини', 'Frostini', '', 2, 4, 4, 'В шейкере со льдом встряхните компоненты.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1641, 'Лимонный Сквош', 'Lemon Squash', '', 2, 2, 4, 'В стакан "тумблер", наполовину заполненный льдом, добавить лимонный сок, сахарную пудру и перемешать. Затем долить содовую и, украсив кружочком лимона, подавать.', null, 0, null, false, false, false, 60, 'tumbler');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1642, 'Фруктовая Жаба', 'Fruity Toad', '', 2, 4, 4, 'В шейкере со льдом встряхнуть персиковый шнапс, трипл сек, кисло-сладкий микс и процедить в бокал. Плеснуть Спрайта или 7Up и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1643, 'Настоящий Скользкий Сосок', 'The Real Slippery Nipple', '', 2, 4, 4, 'В рюмку налить Айриш крем, в середину его, ирисовый шнапс, пусть смешаются. Важно, чтобы уровень жидкости наполнял рюмку почти до краев. В центр рюмки капнуть одну каплю сиропа гренадин, чтобы он завершил вид соска.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1644, 'Фруктовое Наслаждение', 'Fruit Pleasure', '', 2, 4, 4, 'Коктейль от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1645, 'Скорпионья Чаша', 'Scorpion Bowl', '', 2, 2, 4, 'В блендер насыпать три чашки колотого льда и влить компоненты,перемешать в течение нескольких секунд на высокой скорости.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1646, 'Фруктовый Коктейль', '', '', 2, 2, 4, 'Встряхнуть все компоненты в шейкере с колотым людом, перелить в винный бокал, украсить фруктами, подавать с соломинкой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1647, 'Скотница Лижущая Клитор', 'Clit Licking Cowgirl', '', 2, 4, 4, 'В рюмку налить одну часть персикового шнапса и наслоить на нее айриш крем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1648, 'Фруктовый Шейк', 'Fruit Shake', '', 2, 2, 4, 'В блендер поместить шесть кубиков льда, один фруктовый йогурт, апельсиновый сок, поляблока и банан. Пробить всё до образования гомогенной массы и перелить в бокал.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1649, 'Сладкая Леди', 'Sweet lady', '', 2, 4, 4, 'В стакане для смешивания со льдом смешать шотландский виски, персиковый бренди и белый крем де какао. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1650, 'Фрут А Ла Ла', 'Fruit a La La', '', 2, 2, 4, 'В блендер поместить 6 вишен без косточек, 6 ягод клубники, 6 ягод малины, один очищенный персик, 12 виноградин, 1 банан, Спрайт или 7Up и водку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1652, 'Невероятный Халк', 'Incredible Hulk', '', 2, 2, 4, 'В рюмке смешать коньяк и ликер Гипнотик. Использовать хорошо охлажденные компоненты.', null, 0, null, false, false, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1653, 'Сладкая Радуга', 'Sweet Rainbow', '', 2, 2, 4, 'Вбокале смешать аапельсиновый сок, сок лимона, гренадин и сироп голубой Кюрасао. Добавить пару кубиков льда и украсить коктейльной вишней.', null, 0, null, false, false, false, 0, 'brandy');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1655, 'Сладкая Шарлотта', 'weet Charlotte', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, зеленый чай, сок лимона и ложечку сахара. Процедить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1656, 'Ханаан', 'Kanaan', '', 2, 2, 4, 'В блендере с небольшим количеством льда налит молоко, разбить яйцо и добавить две ложки меда. Взбить, перелить в бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1657, 'Сладкие Шоколадные Губки', 'Sweet Chocolate Lips', '', 2, 4, 4, 'В шейкере со льдом смешать ликеры и молоко и перелить в коктейльный стакан, края которого облить шоколадным сиропом.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1658, 'Харакири', 'Hari Kari', '', 2, 2, 4, 'В шейкере со льдом встряхнуть бренди, трипл сек и апельсиновый сок. Процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1683, 'Сломанная Шпора', 'Broken Spur', '', 2, 4, 4, 'В стакане со льдом смешать красный портвейн, красный сладкий вермут и трипл сек. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1660, 'Харви Воллбэнгер', 'Harvey Wallbanger', '', 2, 2, 4, 'В стакан со льдом налить водку, апельсиновый сок и добавить Гальяно.
