INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1667, 'Слаломист', 'Downhill Racer', '', 2, 4, 4, 'В шейкере со льдом встряхнуть пряный ром, амаретто и ананасовый сок. Процедить в охлажденный бокал для коктейлей и подавать.', null, 0, null, false, false, false, 0, 'martini');
