INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1670, 'Херовый Греческий', 'Fucked Up Greek', '', 2, 4, 4, 'В шейкере со льдом налить самбуку и лимонад. Встряхнуть и процедить в рюмку.
