INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1672, 'Хет Трик', 'Hat Trick', '', 2, 4, 4, 'Встакане для смешивания со льдом перемешать светлый ром, сладкий вермут, плеснуть апельсиновый биттер и процедить в бокал. Украсить спиралькой из цедры лимона и подавать.', null, 0, null, false, false, false, 0, 'martini');
