INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1821, 'Танцующая Кукла', 'Dancing Doll', '', 2, 4, 4, 'Одну стопку хорошенько остудить в морозилке, а вторую нагреть в горячей воде.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1823, 'Таракан', 'Cockroach', '', 2, 4, 4, 'Коктейль готов, но перед употреблением рекомендуется спеть:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1824, 'Чешский Сотрудник', 'Czech Collaborator', '', 2, 4, 4, 'Налить в рюмку равные части Бехеровки и коричного шнапса, тщательно перемешать и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1825, 'ТБТ', 'TBT', '', 2, 4, 4, 'В рюмку налить трипл сек, наслоить на него банановый ликер (Creme de Banana) и сверху наслоить текилу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1826, 'Чи Чи', 'Chi Chi', '', 2, 2, 4, 'В блендере со льдом взбить водку, кокосовый крем, ананасовый сок и колотый лёд.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1827, 'Твою Мать!', 'Motherfucker', '', 2, 4, 4, 'В рюмку налить крепкий ром и абсент, в равных долях. Поджечь и накрыть стаканом. Когда пламя погаснет ввести в стакан не переворачивая его соломинку и вдохнуть дым. Задержать дыхание, выпить содержимое рюмки и на выдохе сказать ...Твою м-ма-ать!', null, 0, null, true, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1828, 'Чингис Тон', 'Genghis Thong', '', 2, 2, 4, 'В бокал со льдом налить водку и абрикосовый сок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1830, 'Чпок', 'пиво с водкой', '', 2, 4, 4, 'В рюмку наливаем 2/3 водки и 1/3 светлого пива.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1831, 'Текила БумБум', 'Tequila BoomBoom', '', 2, 4, 4, 'Налить в бокал текилу и накапать соус Табаско, затем налить тоник так, чтобы до краев оставалось около сантиметра и подавать не перемешивая.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1833, 'Текила и Тоник', 'Tequila and Tonic', '', 2, 2, 4, 'В стакан со льдом налить золотую текилу и дополнить его тоником.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1834, 'Чубака', 'Chewbacca', '', 2, 4, 4, 'Перед употреблением следует издать несколько криков на языке Шириивук, который похож на собачий рык и лай. Люди говорить на этом языке не могут, но могут понять его.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1858, 'Шизофрения', 'Schizophrenia', '', 2, 4, 4, 'В пивной бокал налить шампанское, а в рюмку абсент.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1835, 'Текила Санрайз', 'Tequila Sunrise', '', 2, 2, 4, 'В бокал кинуть несколько кубиков льда, залить текилу, апельсиновый сок, после чего добавить несколько капель сиропа гренадина и осторожно помешать с помощью мешалки (для достижения эффекта "восхода солнца").
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1836, 'Чуров', 'Churov', '', 2, 4, 4, 'Подавать не сильно перемешивая.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1837, 'Яблочная Телега', 'Apple Cart', '', 2, 2, 4, 'Небольшой высокий стакан, наполовину заполнить льдом. Влить в него кальвадос, трипл сек и сок лимона. Хорошенько перемешать и подвать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1838, 'Абсолют Санто', 'Absolut Santo', '', 2, 2, 4, 'В шейкере со льдом смешать водку и малиновый ликер, процедить в коктейльный бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1839, 'Темная Сторона', 'Darkside', '', 2, 2, 4, 'Бокал Коллинз наполнить н половину льдом. Влить в него Егермейстер и дополнить колой. Украсить коркой лимона и подавать с соломинкой.', null, 0, null, false, false, false, 40, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1840, 'Шамбро и Шампань', 'Chambord and Champagne', '', 2, 2, 4, 'В бокал для шампанского налить малиновый ликер (Chambord raspberry liqueur) и дополнить шампанским. Для украшения можно использовать клубнику или малину.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1841, 'Темное с Черной', 'Stout and Black', '', 2, 2, 4, 'В пивной бокал налить черносмородиновый сироп и дополнить темным пивом.', null, 0, null, false, false, false, 0, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1842, 'Шампанское Блиц', 'Champagne Blitz', '', 2, 4, 4, 'В бокал налить мятный ликер (White creme de menthe) и дополнить его охлажденным шампанским.', null, 0, null, false, false, false, 60, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1843, 'Теплый Яблочный Пирог', 'Warm Apple Pie', '', 2, 4, 4, 'В рюмку послойно налить водку, яблочный шнапс и коричный шнапс, сверху украсить взбитыми сливками и подавать.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1844, 'Шампанское с Егермейстером', 'Jagermeister Champagne', '', 2, 2, 4, 'В бокал для шампанского налить Егермейстер и дополнить шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1845, 'Tic - Tac', 'Тик Так', '', 2, 4, 4, 'В рюмку налить зеленый мятный ликер и наслоить на него самбуку.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1846, 'Оранж Шампань', 'Orange Champagne', '', 2, 2, 4, 'В охлажденный бокал налить немного <span class="st">оранжевого Кюрасао (<em>Orange Cura&ccedil;ao</em>)</span> и дополнить холодным шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1847, 'Апельсиновый Тик-Так', '', '', 2, 2, 4, 'В высокий стакан, на 3/4 заполненный кубиками льда, влить Ром и Энергетический напиток.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1849, 'Тирамису', 'Tiramisu', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, кофе, калуа, нежирные сливки, перелить в коктейльный бокал и посыпать тертым шоколадом.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1850, 'Шарм', 'Сharm', '', 2, 2, 4, 'В блендере взбиваем арбуз. Можно добавить сахара, и это часто оправдано.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1852, 'Бананово-Клубничный Шейк', 'Banana Strawberry Shake', '', 2, 2, 4, 'В шейкере взбить равные части бананов и клубники  свежевыжатого  яблочного сока.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1854, 'Шелковые Чулки', 'Silk Stockings', '', 2, 4, 4, 'В шейкере со льдом встряхнуть текилу, какао ликер, сливки и гренадин, процедить в бокал и, посыпав тертой корицей, подавать.', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1855, 'То, что Доктор Прописал', 'Just What the Doctor Ordered', '', 2, 2, 4, 'В пивной стакан или кружку налить кофейный ликер, вишневый сироп, пиво и дополнить колой. Используйте охлаждённые напитки.', null, 0, null, false, false, false, 50, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1856, 'Шери Шампань', 'Cherry Champagne', '', 2, 2, 4, 'В бокал налить шери бренди и дополнить его охлажденным шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1857, 'Товарищ', 'Tovarisch', '', 2, 4, 4, 'В шейкере со льдом встряхнуть водку, ликер Кюммель и сок половинки лайма. Процедить в рюмки и подавть.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1861, 'Токийский Чай', 'Tokyo Tea', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, джин, текилу, ром, трипл сек, и 30 мл дынного ликера, перелить в стакан со льдом и подавть.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1862, 'Шипучий Гранат', 'Sparkling Pomegranate', '', 2, 2, 4, 'В бокале со льдом смешать гранатовый сок, водку и отжать дольку лимона. Дополнить содовой, чуть смешать и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1863, 'Толстожопый', 'Fatass', '', 2, 4, 4, 'В рюмке смешать равные части малиновой водки и лимонного рома.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1866, 'Ширли Темпл', 'Shirley Temple', '', 2, 2, 4, 'В стакан со льбдом налить гренадин и дополнить его имбирым элем. Украсить долькой лимона или апельсина и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1867, 'Том Коллинс', 'Tom Collins', '', 2, 2, 4, 'В шейкере со льдом встряхнуть джин, сахарный сироп, сок лимона и перелить в бокал, заполненный льдом. Дополнить содовой, украсить лимоном или апельсином и подавать.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1868, 'Шмель Шот', 'Bumble Bee Shot', '', 2, 4, 4, 'В рюмку налить айриш крем, по ложечке наслоить на него кофейный ликёр (Kahlua, например) и сверху наслоить самбуку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1878, 'Шоколадный Пират', 'Chocolate Pirate', '', 2, 2, 4, 'В блендере взбить все компоненты до приобретения муссообразной консистенции, перелить в винный бокал и подавать.', null, 0, null, false, false, false, 0, 'wine');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1879, 'Топливо для Джипа', 'Jeep Fuel', '', 2, 4, 4, 'В рюмку налить виски (Джек Дэниелс), текилу и темный ром в равных пропорциях.', null, 0, null, false, false, false, 40, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1880, 'Шоколадный Торт', 'Chocolate Cake', '', 2, 4, 4, 'В шейкере со льдом встряхнуть арахисовый ликер и водку, перелить в рюмку. подавать с лимоном.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1881, 'Топливо Счастья', 'Happy Fuel', '', 2, 4, 4, 'В коктейльный бокал налить водку, апельсиновый сок и дополнить энергетическим напитком. Все компоненты должны быть холодными.', null, 0, null, false, false, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1883, 'Топор', 'Axe', '', 2, 4, 4, 'Отрубает, как настоящим топором!', null, 0, null, false, false, false, 50, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1884, 'Шокоминт', 'Chocomint', '', 2, 2, 4, 'В бокал налить мятный ликер и дополнить шоколадным молоком.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1885, 'Торнадо', 'Tornado', '', 2, 4, 4, 'В коктейльный бокал положить половину ложечки сахара и три кубика льда. Смешать виски, ром, текилу и водку и перелить в бокал. дополнить колой и подавать.', null, 0, null, false, false, false, 40, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1886, 'Шоссе в Ад', 'Highway To Hell', '', 2, 4, 4, 'На дно рюмки положить ложечку коричневого сахара. Аккуратно, по стеночке налить текилу, на нее наслоить самбуку, и на нее - абсент.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1888, 'Шот - Южный Заворот', 'A Short Southern Screw', '', 2, 4, 4, 'На дно рюмки налить персиковый ликер, затем наслоить водку и сверху капнуть апельсиновой водки.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1889, 'Торцевой Ключ', 'End Wrench', '', 2, 2, 4, 'В бокал со льдом налить равные части апельсинового сока и тоника.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1890, 'Шот Любви Зловонной', 'Shot of Love Stinks', '', 2, 4, 4, 'Процедить в рюмку и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1891, 'Точка', 'Dot', '', 2, 2, 4, 'Вбокал нливается текила, абсент и энергетический напиток. В отдельную рюмку наливается самбука.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1892, 'Шпрингбок', 'Springbok', '', 2, 4, 4, 'Подавать.', null, 0, null, false, true, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1893, 'Точка Джи', 'G- Spot', '', 2, 4, 4, 'В шейкере со льдом встряхнуть равные части Егермействера, трипл сек и дынного ликера. Процедть в стопку и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1894, 'Шрек', 'Shrek', '', 2, 4, 4, 'В шейкер со льдом налить водку, дынный ликер, персиковый шнапс, кокосовый ром, Спрайт или 7Up и ананасовый сок. Разлить по большим рюмкам и подавать.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1895, 'Тощий Мексиканец', 'Skinny Mexican', '', 2, 2, 4, 'В стакан со льдбом влить одну - полторы рюмки кофейного ликера (kahlua) и дополнить диетической колой.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1896, 'Шторм', 'Storm', '', 2, 2, 4, 'Все ингредиенты смешайте в шейкере, перелейте в небольшой бокал хайболл со льдом. Украсьте веточкой мяты и подавайте.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1897, 'Трансформер', 'Transformer', '', 2, 2, 4, 'В стакан со льдом налить водку, затем апельсиновый сок и в конце - ликер Гипнотик (Hpnotiq liqueur).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1898, 'Штормовое Облако', 'Stormcloud', '', 2, 4, 4, 'В рюмку налить амаретто, наслоить на него айриш крем и на него 151 ром.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1899, 'Требуется Помощник', 'Help Wanted', '', 2, 2, 4, 'В шейкере со льдом встряхнуть бурбон, абрикосовый бренди и лимонный сок. Процедитьв бокал и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1942, 'Эль Президенте', 'El Presidente', '', 2, 4, 4, 'В шейкере со льдом встряхнуть светлый ром, сухой вермут, апельсиновый ликер и гренадин. Процедить в бокал, украсить спиралькой из кожуры апельсина и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1900, 'Шуруповерт', 'Power Drill', '', 2, 2, 4, 'Стакан на половину наполнить льдом. Налить водки до уровня льда. Влить энергетический напиток, на два пальца ниже края. Дополнить свежевыжатым апельсиновым соком и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1901, 'Три - Два - Один', '3 - 2 - 1', '', 2, 4, 4, 'В стакан для смешивания со льдом добавить три части виски, две части желтого Шартрёза и одну часть сухого вермута.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1902, 'Шурупчик', 'Screwie', '', 2, 2, 4, 'В стакан налить шампанское и в него "ввинтить" настойку рябины на коньяке. Добавть лёд, дольку лимона и подавать.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1903, 'Три Волны', 'Three Waves', '', 2, 4, 4, 'Вбольшую рюмку налить горячий эспрессо, на него наслоить кофейный ликер и постараться наслоить на него айриш крим.', null, 0, null, false, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1904, 'Щербет на Завтрак', 'Breakfast Sherbet', '', 2, 2, 4, 'В блендере всбить до гомогенного состояния охлажденные и очищенные дыню и киви, сок лайма и сахар. Поместить в чашу и укрыв крышкой поставить в момрозильник до того момента, пока масса не начнет замерзать и твердеть. Тогда влить в нее холодное молоко и хорошенько перемешать миксером, после чего полученную массу разложить по бокалам и подавать.', null, 0, null, false, false, false, 0, 'coffee');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1905, 'Три Мудреца', 'three wise men', '', 2, 4, 4, 'В рюмке смешать три разных типа виски.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1906, 'Эвершок', 'Everscock', '', 2, 4, 4, 'В рюмке смешать равные части спирта коричного шнапса. Подавать. Пить залпом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1907, 'Триколор', 'Tricolor', '', 2, 2, 4, 'В кувшин со льдом налить одну часть вишневого сока, на него по ложечке наслоить одну часть голубого энергетического напитка, и на него уложить слой Спрайта', null, 0, null, false, true, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1956, 'Эпплтини', 'Appletini', '', 2, 4, 4, 'В шейкере со льдом встряхнуть яблочную водку и яблочный ликер из кислых яблок. Процедить в бокал и подавать.', null, 0, null, false, false, false, 60, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1908, 'Эврибадис Айриш', 'Everybody&#039;s Irish', '', 2, 4, 4, 'В шейкере со льдом встряхнуть ирландский виски, зеленый Шартрёз и зеленый Крем де Менте, процедить в коктейльный бокал, добавить оливку и подавать. Можно украсить кружочком из лайма.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1909, 'Грапповый Трилистник', 'Grappa Gimlet', '', 2, 2, 4, 'В стакан со льдом налить граппу, джин и сок лайма. Перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1911, 'Тройка', '', '', 2, 4, 4, 'В бокал для шампанского налить водки и коньяка, затем шампанского.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1913, 'Тропический Экстаз', 'Tropical Ecstasy', '', 2, 2, 4, 'В бокал со льдом налить равные части водки, дынного ликера, кокосового молока и голубого Кюрасао.', null, 0, null, false, false, false, 0, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1914, 'Эй. Джей.', 'A. J.', '', 2, 4, 4, 'В шейкере со льдом встряхнуть равные части яблочного бренди и грейпфрутового сока. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1915, 'Тропический Эль', 'Tropical Ale', '', 2, 2, 4, 'В стакан со льдом налить равные части ананасовго сока и имбирного эля, добавить гренадина до получения ярко-красного цвета.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1916, 'Эйфелева Башня', 'Tour Eiffel', '', 2, 4, 4, 'В металлическую посуду наливаем абсент, поджигаем его и даем погореть несколько секунд. Выливаем горящий абсент на получившуюся у нас башню. Он стекает в рюмки, которые и подаем, убрав с них бутылку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1917, 'Тростниковая Жаба', 'Cane Toad', '', 2, 2, 4, 'Налить в стакан водку, текилу, шери бренди и содовую. Подавать с соломинкой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1943, 'Жирный Ублюдок', 'Fat Bastard', '', 2, 2, 4, 'В стакан со льдом налить водку, белый шоколадный ликер и молоко. Немного перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1919, 'Трубопровод', 'Pipeline', '', 2, 4, 4, 'В шейкере со льдом встряхнуть светлый ром, абрикосовый бренди, пол ложечки сахарной пудры и лимонный сок. Процедить в охлажденный бокал и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1920, 'H.D.', '', '', 2, 2, 4, 'В пивной бокальчик налить виски и айриш крем, перемешать и дополнить кофе.', null, 0, null, false, false, false, 0, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1921, 'Туббюки', 'Tubboocki', '', 2, 4, 4, 'В рюмку налить Галиано, бурбон и самбуку.', null, 0, null, false, false, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1922, 'Экватор', 'Equator', '', 2, 4, 4, 'В стопку налить трипл сек, сверху, при помощи коктейльной ложечки, наслоить текилу, и добавить пять капель соуса Табаско (соус проходит через Текилу и останавливается на границе между текилой и трипл секом).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1923, 'Большой Тупой Русский', 'Big Dumb Russian', '', 2, 2, 4, 'В пивной бокал положить лёд, затем налить водку, на две трети спрайта или 7Up, ананасовый сок и голубой курасао, до получения нежно-голубого оттенка.', null, 0, null, false, false, false, 0, 'beer');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1924, 'Эквилириум', 'Equilibrium', '', 2, 2, 4, 'В блендере пробить мороженое, молоко, банан и немного льда. Получившуюся массу разделить на два шейкера, в один добавить гренадин, а в другой сироп голубой Кюрасао. Встряхнуть и одновременно, с двух сторон влить в бокал, чтобы получились вертикальные слои.', null, 0, null, false, true, false, 50, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1925, 'Турбина', 'Turbine', '', 2, 4, 4, 'В бокал для шампанского налить абсент, самбуку и дополнить сухим шампанским.', null, 0, null, false, false, false, 0, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1955, 'Удовольствие', 'Pleasure', '', 2, 4, 4, 'РАзливаем по бокалам, украшаем кружками лимона и подаем.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1927, 'Турбо-Самбука', 'Turbo-Sambuca', '', 2, 4, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1928, 'Экспресс', 'Express', '', 2, 4, 4, 'В шейкере со льдом или в стакане со льдом смешать водку и Гран Марнье. Процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'martini');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1979, 'Ураган', '', '', 2, 2, 4, 'Для приготовления коктейля лучше использовать свежевыжатый сок из половины ананаса и двух яблок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1930, 'Экстази', 'Extasy', '', 2, 2, 4, 'Если чаша прозрачная, то по стеночке влить голубой Кюрасао, тогда коктейль будет двуслойным и красивым на вид.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1931, 'Тюрьма Сэмми', 'Sammy Slammer', '', 2, 4, 4, 'В рюмку налить амаретто, наслоть на него текилу а на нее крепкий ром. Поджешь и подавать.', null, 0, null, true, true, false, 0, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1933, 'Тяжелая вода', 'Heavy Water', '', 2, 4, 4, 'В шейкере встряхните все компоненты.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1934, 'Электролит', 'Electrolyte', '', 2, 2, 4, 'В шейкер поместить кусочки арбуза, без косточек и корки. Размять, добавить лёд, ананасовый сок, сок лимона и водку. хорошенько встряхнуть и процедить в бокал со льдом. Украсить долькой арбуза и подавать.', null, 0, null, false, false, false, 60, 'champagne');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1936, 'Элис', 'Alice', '', 2, 2, 4, 'Перелить в бокал, украсить фруктами и подавать.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1939, 'Убийца', 'The Killer', '', 2, 4, 4, '', null, 0, null, false, false, false, 60, 'shot');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1944, 'Эльдорадо', 'Eldorado', '', 2, 2, 4, 'В шейкере со льдом втряхнуть текилу, мёд и сок лимона. Процедить в бокал со льдом и подавать.', null, 0, null, false, false, false, 60, 'collins');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1946, 'Эльфийская кровь', 'Elven Blood', '', 2, 2, 4, 'В завершение плесните кокосового ликёра.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1947, 'Убунтини', 'Ubuntini', '', 2, 4, 4, 'В бокал налить водку, тоник, виноградный сок, и в завершение плеснуть немного виски. Вместо виноградного сока можно использовать грейп соду - если найдёте ее в наших магазинах.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1948, 'Эльфийский Чай', 'Elf Tea', '', 2, 2, 4, 'В чашку со льдом налить мфтный ликер, айриш крем и дополнить чаем. Можно использовать фрутовый ледяной чай.', null, 0, null, false, false, false, 50, 'coffee');
