INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2031, 'Мексиканский флаг', 'Banderas de Mexico', '', 2, 4, 4, 'В одну рюмку налить томатный сок и накапать табаско. Во вторую - текилу. В третью сок лайма. Пить заслпом одну за одной.
